// filename: JDResultCard.js

import React from "react";
import PropTypes from 'prop-types'; // Import PropTypes for prop validation

export default function JDResultCard({ result, onReset }) {
  // --- Helper function to determine text color based on score ---
  const getScoreColor = (score) => {
    if (score >= 80) return "text-success"; // Green for high scores
    if (score >= 60) return "text-warning"; // Yellow for medium scores
    return "text-danger"; // Red for low scores
  };

  // --- Safely destructure data from the result prop with fallbacks ---
  const { 
    jd_analysis: jdAnalysis = {}, 
    job_description: jobInfo = {},
    experience: resumeExperience = 0
  } = result || {};

  // --- Utility to format and truncate skill lists for display ---
  const formatSkills = (skills) => {
    if (!skills || !Array.isArray(skills)) return [];
    return skills.slice(0, 20); // Show a maximum of 20 skills to keep UI clean
  };
  
  const scoreItems = [
    { label: "Skills Match", value: jdAnalysis.skill_match, theme: "success" },
    { label: "Experience Match", value: jdAnalysis.experience_match, theme: "info" },
    { label: "Education Match", value: jdAnalysis.education_match, theme: "warning" },
    { label: "Keyword Match", value: jdAnalysis.keyword_match, theme: "primary" }
  ];

  return (
    <div className="result-card-container">
      <div className="container" style={{ maxWidth: '800px' }}>
        
        {/* Job Title Header */}
        {jobInfo.job_title && jobInfo.job_title !== "Unknown Position" && (
          <div className="card shadow-lg mb-4">
            <div className="card-body text-center p-4">
              <h4 className="card-title fw-bold mb-2">Job Match Analysis</h4>
              <p className="text-muted fs-5 mb-0">
                For: <strong>{jobInfo.job_title}</strong>
                {/* CORRECTED: Use 'experience_required' from jobInfo */}
                {jobInfo.experience_required > 0 && (
                  <span className="ms-3"> | Required Experience: <strong>{jobInfo.experience_required}+ years</strong></span>
                )}
              </p>
            </div>
          </div>
        )}

        {/* Overall Score Card */}
        <div className="card shadow-lg mb-4">
          <div className="card-body p-4">
            <h4 className={`card-title fw-bold text-center mb-4 ${getScoreColor(jdAnalysis.overall_score)}`}>
              Overall Match Score: {jdAnalysis.overall_score}%
            </h4>
            <div className="row text-center">
              {scoreItems.map((item) => (
                <div key={item.label} className="col-md-3 col-6 mb-3">
                  <h6 className="mb-2">{item.label}</h6>
                  {/* IMPROVEMENT: Added a themed border color for better visual distinction */}
                  <div
                    className={`mx-auto rounded-circle d-flex align-items-center justify-content-center border-4 border-${item.theme}`}
                    style={{
                      width: "80px",
                      height: "80px",
                      background: "linear-gradient(135deg, #f8f9fa, #e9ecef)",
                    }}
                  >
                    <span className={`fw-bold fs-5 ${getScoreColor(item.value)}`}>{item.value}%</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Skills Analysis Section */}
        <div className="row">
          {/* Matching Skills */}
          {jdAnalysis.matching_skills?.length > 0 && (
            <div className="col-md-6 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title fw-bold text-success mb-3">
                    ✅ Matching Skills ({jdAnalysis.matching_skills.length})
                  </h5>
                  <div className="d-flex flex-wrap gap-2">
                    {formatSkills(jdAnalysis.matching_skills).map((skill, index) => (
                      <span key={index} className="badge bg-success-subtle text-success-emphasis rounded-pill px-2 py-1">{skill}</span>
                    ))}
                    {jdAnalysis.matching_skills.length > 20 && (
                      <span className="badge bg-info-subtle text-info-emphasis rounded-pill px-2 py-1">+{jdAnalysis.matching_skills.length - 20} more</span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Missing Skills */}
          {jdAnalysis.missing_skills?.length > 0 && (
            <div className="col-md-6 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title fw-bold text-danger mb-3">
                    ❌ Missing Skills ({jdAnalysis.missing_skills.length})
                  </h5>
                  <div className="d-flex flex-wrap gap-2">
                    {formatSkills(jdAnalysis.missing_skills).map((skill, index) => (
                      <span key={index} className="badge bg-danger-subtle text-danger-emphasis rounded-pill px-2 py-1">{skill}</span>
                    ))}
                    {jdAnalysis.missing_skills.length > 20 && (
                      <span className="badge bg-warning-subtle text-warning-emphasis rounded-pill px-2 py-1">+{jdAnalysis.missing_skills.length - 20} more</span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Experience & Education Summary */}
        <div className="row">
          {/* Experience Summary */}
          <div className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h6 className="fw-bold">Experience Match</h6>
                {/* ACCESSIBILITY: Added aria attributes to progress bar */}
                <div className="progress mb-2" style={{ height: "20px" }} role="progressbar" aria-label="Experience match" aria-valuenow={jdAnalysis.experience_match} aria-valuemin="0" aria-valuemax="100">
                  <div className="progress-bar bg-info" style={{ width: `${jdAnalysis.experience_match}%` }}></div>
                </div>
                <small className="text-muted">
                  Your experience: {resumeExperience} years | Required: {jobInfo.experience_required > 0 ? `${jobInfo.experience_required} years` : "N/A"}
                </small>
              </div>
            </div>
          </div>

          {/* Education Summary */}
          <div className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h6 className="fw-bold">Education Match</h6>
                 {/* ACCESSIBILITY: Added aria attributes to progress bar */}
                <div className="progress mb-2" style={{ height: "20px" }} role="progressbar" aria-label="Education match" aria-valuenow={jdAnalysis.education_match} aria-valuemin="0" aria-valuemax="100">
                  <div className="progress-bar bg-warning" style={{ width: `${jdAnalysis.education_match}%` }}></div>
                </div>
                <small className="text-muted">
                  {jdAnalysis.education_match >= 50 ? "✅ Education seems to match requirements" : "⚠️ May not meet education requirements"}
                </small>
              </div>
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="text-center mt-3">
          <button className="btn btn-primary me-3" onClick={onReset}>
            Analyze Another
          </button>
          <button className="btn btn-outline-secondary" onClick={() => window.print()}>
            Print Report
          </button>
        </div>

      </div>
    </div>
  );
}

// ROBUSTNESS: Added PropTypes for type checking and ensuring required props are passed.
JDResultCard.propTypes = {
  result: PropTypes.shape({
    jd_analysis: PropTypes.shape({
      overall_score: PropTypes.number,
      skill_match: PropTypes.number,
      experience_match: PropTypes.number,
      education_match: PropTypes.number,
      keyword_match: PropTypes.number,
      matching_skills: PropTypes.arrayOf(PropTypes.string),
      missing_skills: PropTypes.arrayOf(PropTypes.string),
    }).isRequired,
    job_description: PropTypes.shape({
      job_title: PropTypes.string,
      experience_required: PropTypes.number,
    }).isRequired,
    experience: PropTypes.number, // Experience from the resume
  }).isRequired,
  onReset: PropTypes.func.isRequired,
};
