import React, { useState } from "react";

export default function ResultCard({ result, onReset }) {
  const [copiedText, setCopiedText] = useState(null);

  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 50) return "text-warning";
    return "text-danger";
  };
  
  const copyToClipboard = (textToCopy, type) => {
    navigator.clipboard.writeText(textToCopy).then(() => {
      setCopiedText(type);
      setTimeout(() => setCopiedText(null), 2000);
    }, (err) => {
      console.error('Failed to copy!', err);
    });
  };

  const renderKeywords = (keywords, type) => {
    if (!keywords || keywords.length === 0) {
      return <p className="text-muted small">None to display.</p>;
    }
    return (
      <div className="d-flex flex-wrap gap-2">
        {keywords.map((keyword, index) => (
          <span key={index} className={`badge ${type === 'matched' ? 'bg-success-soft' : 'bg-danger-soft'}`}>
            {keyword}
          </span>
        ))}
      </div>
    );
  };

  return (
    <>
      <style>
        {`
        .result-card-container {
            background: linear-gradient(135deg, rgba(255, 255, 255, 0.2) 0%, rgba(240, 244, 248, 0.1) 100%);
            padding: 2rem 1rem;
            display: flex;
            flex-direction: column;
            align-items: center;
            border-radius: 1rem;
            justify-content: center;
            backdrop-filter: blur(10px);
        }
        .custom-card {
            background: rgba(255, 255, 255, 0.85);
            backdrop-filter: blur(2px);
            border-radius: 1rem !important;
            border: none;
        }
        .score-circle {
            background: linear-gradient(135deg, #000000ff, #858586ff);
        }
        .score-circle h1 {
            color: #ffffffff;
        }
        .list-group-item {
            background-color: transparent;
            border-color: rgba(0, 0, 0, 0.1);
        }
        .bg-success-soft {
            background-color: rgba(25, 135, 84, 0.2);
            color: #0f5132;
            font-weight: 500;
        }
        .bg-danger-soft {
            background-color: rgba(220, 53, 69, 0.2);
            color: #842029;
            font-weight: 500;
        }
        `}
      </style>
      <div className="result-card-container">
        <div className="container" style={{ maxWidth: '600px' }}>
          
          <div className="card custom-card shadow-lg mb-4">
            <div className="card-body text-center p-4">
              <h4 className="card-title fw-bold mb-3">Overall Resume Score</h4>
              <div
                className={`mx-auto d-flex align-items-center justify-content-center rounded-circle shadow score-circle ${getScoreColor(result.overall_score)}`}
                style={{ width: "160px", height: "160px" }}
              >
                <h1 className="fw-bold mb-0">{result.overall_score}/100</h1>
              </div>
            </div>
          </div>

          {result.job_match_analysis && (
            <div className="card custom-card shadow-lg mb-4">
                <div className="card-body p-4">
                    <h4 className="card-title fw-bold text-center mb-3">Job Description Match</h4>
                    <div className="text-center mb-4">
                        <h2 className={`fw-bold ${getScoreColor(result.job_match_analysis.match_score)}`}>
                            {result.job_match_analysis.match_score}%
                        </h2>
                        <p className="text-muted mb-0">Match Score</p>
                    </div>
                    <div className="mb-3">
                        <h6 className="fw-bold">✅ Matched Keywords</h6>
                        {renderKeywords(result.job_match_analysis.matched_keywords, 'matched')}
                    </div>
                    <div className="mb-3">
                        <h6 className="fw-bold">❌ Missing Keywords</h6>
                        {renderKeywords(result.job_match_analysis.missing_keywords, 'missing')}
                    </div>
                    {result.job_match_analysis.feedback && (
                       <div>
                         <h6 className="fw-bold">💡 Feedback</h6>
                         <p className="mb-0">{result.job_match_analysis.feedback}</p>
                       </div>
                    )}
                </div>
            </div>
          )}

          <div className="card custom-card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold">Contact Information</h5>
               <ul className="list-group list-group-flush">
                 {["email", "phone", "linkedin", "github"].map((k) => {
                   const val = result.contacts?.[k];
                   return (
                     <li className="list-group-item d-flex justify-content-between align-items-center" key={k}>
                       <span>
                         {k === "email" && "📧 Email:"} {k === "phone" && "📞 Phone:"}
                         {k === "linkedin" && "🔗 LinkedIn:"} {k === "github" && "🐙 GitHub:"}
                         &nbsp;<b>{val || "Not found"}</b>
                       </span>
                       {val && (
                         <button className="btn btn-sm btn-outline-secondary" onClick={() => copyToClipboard(val, k)}>
                           {copiedText === k ? "Copied!" : "Copy"}
                         </button>
                       )}
                     </li>
                   );
                 })}
               </ul>
            </div>
          </div>

          {/* --- THIS SECTION WAS RESTORED --- */}
          <div className="card custom-card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold">Sections Found</h5>
              <ul className="list-group list-group-flush">
                {result.metrics?.sections_found && Object.entries(result.metrics.sections_found).map(
                  ([section, present]) => (
                    <li key={section} className="list-group-item d-flex justify-content-between">
                      {section.charAt(0).toUpperCase() + section.slice(1)}
                      <span className={`badge ${present ? "bg-success" : "bg-danger"}`}>
                        {present ? "Present" : "Missing"}
                      </span>
                    </li>
                  )
                )}
              </ul>
            </div>
          </div>

          {/* --- THIS SECTION WAS RESTORED --- */}
          <div className="card custom-card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold">Suggestions</h5>
              {result.suggestions?.length ? (
                <ul className="list-group list-group-flush">
                  {result.suggestions.map((s, i) => (
                    <li key={i} className="list-group-item">
                      💡 {s}
                    </li>
                  ))}
                </ul>
              ) : (
                <div className="p-3 text-center">
                  <h6 className="mb-0 text-success">✅ No major suggestions. Your resume looks well-structured!</h6>
                </div>
              )}
            </div>
          </div>

          <div className="text-center mt-4">
            <button className="btn btn-secondary" onClick={onReset}>
              Analyze Another Resume
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
