import React, { useState } from "react";
import UploadForm from "./components/UploadForm";
import ResultCard from "./components/ResultCard";
import JDUploadForm from "./components/JDUploadForm";
import JDResultCard from "./components/JDResultCard"; // NEW - Import JD result card
// ✅ Import your local background image
import bgImage from "./background.png"; // replace with your actual file name

// --- ⬇️ 1. IMPORT YOUR FOUR RESUME SAMPLES HERE ---
// Make sure these files are in your src folder
import resumeSample1 from './sample1.jpg';
import resumeSample2 from './sample2.png';
import resumeSample3 from './sample3.png';
import resumeSample4 from './sample4.jpg';

export default function App() {
  const [result, setResult] = useState(null);
  const [showModal, setShowModal] = useState(null); // 'template', 'instructions', or 'jd-upload'
  const [analysisMode, setAnalysisMode] = useState("resume"); // 'resume' or 'jd'

  // --- Create an array of the imported resume images for easier mapping ---
  const resumeSamples = [
    { id: 1, src: resumeSample1, alt: "Sample Resume 1" },
    { id: 2, src: resumeSample2, alt: "Sample Resume 2" },
    { id: 3, src: resumeSample3, alt: "Sample Resume 3" },
    { id: 4, src: resumeSample4, alt: "Sample Resume 4" },
  ];

  const handleReset = () => {
    setResult(null);
    setAnalysisMode("resume");
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const closeModal = () => {
    setShowModal(null);
  };

  const handleJDAnalysisComplete = (result) => {
    setResult(result);
    setAnalysisMode("jd");
    closeModal();
    
    // Smoothly scroll to results
    setTimeout(() => {
      const resultElement = document.getElementById("result-container");
      if (resultElement) {
        resultElement.scrollIntoView({ behavior: "smooth" });
      }
    }, 100);
  };

  return (
    <>
      <style>
        {`
          body {
            background-color: #999b9cff; /* fallback */
            margin: 0;
            padding: 0;
          }

          .app-container {
            min-height: 100vh;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: flex-start;
            padding: 3rem 1rem;

            /* --- BACKGROUND STYLES --- */
            background-image: url(${bgImage});
            background-size: cover;
            background-position: center;
            background-repeat: no-repeat;
            background-attachment: fixed;
          }

          .content-card {
            background: linear-gradient(135deg, rgba(255, 255, 255, 0.8) 0%, rgba(240, 244, 248, 0.1) 100%);
            border-radius: 1.5rem;
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
            transition: all 0.3s ease;
          }

          .app-title {
            font-size: 4.5rem;
            font-weight: 700;
            color: #e0e0e0;
            text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
            transition: opacity 0.5s ease;
          }

          .app-subtitle {
            font-size: 1.5rem;
            color: #bbbbbb;
            text-shadow: 1px 1px 2px rgba(0,0,0,0.4);
            transition: opacity 0.5s ease;
          }

          @keyframes fadeInUp {
            from {
              opacity: 0;
              transform: translateY(20px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .animate-on-load {
            animation: fadeInUp 0.8s ease-in-out forwards;
          }

          .animate-on-load.delay-1 {
            animation-delay: 0.2s;
          }

          /* Navbar Styles */
          .navbar-custom {
            background: rgba(0, 0, 0, 0.4);
            backdrop-filter: blur(10px);
            width: 100%;
            position: fixed;
            top: 0;
            z-index: 1000;
            padding: 0 !important; /* Remove all padding */
            margin: 0 !important; /* Remove all margin */
          }
          
          .navbar-custom .container {
            padding: 0 !important;
            margin: 0 !important;
          }
          
          .navbar-custom .navbar-nav {
            padding: 0;
            margin: 0;
          }
          
          .navbar-custom .nav-item {
            padding: 0;
            margin: 0;
          }
          
          .navbar-brand {
            color: #e0e0e0 !important;
            font-weight: 600;
            font-size: 1.2rem;
            padding: 0.5rem 1rem !important; /* Adjust as needed */
            margin: 0 !important;
          }
          
          .nav-link-custom {
            color: #bbbbbb !important;
            padding: 0.5rem 1rem !important;
            border-radius: 0.5rem;
            transition: all 0.3s ease;
            margin: 0 !important;
          }
          
          .nav-link-custom:hover {
            color: #ffffff !important;
            background: rgba(255, 255, 255, 0.1);
          }

          .nav-link-custom.active {
            color: #ffffff !important;
            background: rgba(255, 255, 255, 0.2);
          }
          
          .navbar-toggler {
            padding: 0.25rem 0.5rem !important;
            margin: 0.5rem !important;
          }
          
          /* Modal Styles */
          .modal-backdrop-custom {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.7);
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 2000;
          }
          
          .modal-content-custom {
            background-color: #333333ff;
            border-radius: 1rem;
            max-width: 90%;
            padding: 1.5rem;
            width: 800px;
            max-height: 80vh;
            overflow-y: auto;
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.4);
            border: 1px solid rgba(255, 255, 255, 0.1);
          }
          
          .modal-header-custom {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 1.5rem;
            padding-bottom: 1rem;
            border-bottom: 1px solid rgba(255, 255, 255, 0.1);
          }
          
          .modal-title-custom {
            color: #e0e0e0;
            font-size: 1.8rem;
            font-weight: 600;
          }
          
          .close-button {
            background: none;
            border: none;
            color: #bbbbbb;
            font-size: 1.5rem;
            cursor: pointer;
            transition: color 0.3s ease;
          }
          
          .close-button:hover {
            color: #ffffff;
          }
          
          .modal-body-custom {
            color: #d0d0d0;
            line-height: 1.6;
          }
          
          .modal-body-custom h4 {
            color: #e0e0e0;
            margin-top: 1.5rem;
            margin-bottom: 0.5rem;
          }
          
          .modal-body-custom ul, .modal-body-custom ol {
            padding-left: 1.5rem;
          }
          
          .modal-body-custom li {
            margin-bottom: 0.5rem;
          }

          /* --- ⬇️ 3. ADDED STYLES FOR RESUME GALLERY --- */
          .resume-gallery {
            display: flex;
            flex-wrap: wrap;
            gap: 1.5rem; /* Space between images */
            justify-content: center;
          }

          .resume-item {
            flex: 1 1 45%; /* On larger screens, two items per row */
            max-width: 45%;
            box-shadow: 0 4px 15px rgba(0,0,0,0.5);
            border-radius: 8px;
            overflow: hidden;
            transition: transform 0.3s ease, box-shadow 0.3s ease;
          }

          .resume-item:hover {
            transform: scale(1.03);
            box-shadow: 0 8px 25px rgba(0,0,0,0.7);
          }
          
          .resume-image {
            width: 100%;
            height: auto;
            display: block;
          }

          /* Mode selector styles */
          .mode-selector {
            display: flex;
            justify-content: center;
            margin-bottom: 1rem;
            background: rgba(255, 255, 255, 0.1);
            border-radius: 0.5rem;
            padding: 0.25rem;
          }
          
          .mode-button {
            margin-left : 0.5rem;
            padding: 0.5rem 1.5rem;
            border: none;
            background: transparent;
            color: #bbbbbb;
            border-radius: 0.25rem;
            cursor: pointer;
            transition: all 0.3s ease;
          }
          
          .mode-button {
            background: rgba(255, 255, 255, 0.2);
            color: #ffffff;
          }
          
          .mode-button:hover:not(.active) {
            background: rgba(255, 255, 255, 0.1);
          }

          @media (max-width: 768px) {
            .app-container {
              padding: 2rem 1rem;
            }
            .app-title {
              font-size: 2rem;
            }
            
            .navbar-brand {
              font-size: 1.2rem;
              padding: 0.5rem !important;
            }
            
            .nav-link-custom {
              padding: 0.4rem 0.8rem !important;
              font-size: 0.9rem;
            }
            
            .modal-content-custom {
              width: 95%;
              padding: 1.5rem;
            }
            
            .modal-title-custom {
              font-size: 2.5rem;
            }

            /* --- Make gallery single-column on mobile --- */
            .resume-item {
              flex: 1 1 100%;
              max-width: 100%;
            }

            .mode-selector {
              flex-direction: column;
              gap: 0.5rem;
            }
          }
        `}
      </style>

      {/* Navbar */}
<nav className="navbar navbar-expand-lg navbar-custom">
  <div className="container-fluid">
    <a 
      className="navbar-brand" 
      href="#"
      onClick={(e) => {
        e.preventDefault();
        handleReset();
      }}
      style={{ cursor: "pointer" }}
    >
      Resume Analyzer
    </a>
    <button
      className="navbar-toggler"
      type="button"
      data-bs-toggle="collapse"
      data-bs-target="#navbarNav"
      aria-controls="navbarNav"
      aria-expanded="false"
      aria-label="Toggle navigation"
    >
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarNav">
      <ul className="navbar-nav ms-auto">
        <li className="nav-item">
          <a 
            className="nav-link nav-link-custom" 
            href="#" 
            onClick={(e) => {
              e.preventDefault();
              setShowModal('template');
            }}
          >
            Sample Resumes
          </a>
        </li>
        <li className="nav-item">
          <a 
            className="nav-link nav-link-custom" 
            href="#" 
            onClick={(e) => {
              e.preventDefault();
              setShowModal('jd-upload');
            }}
          >
            JD Analysis
          </a>
        </li>
        <li className="nav-item">
          <a 
            className="nav-link nav-link-custom" 
            href="#" 
            onClick={(e) => {
              e.preventDefault();
              setShowModal('instructions');
            }}
          >
            Instructions
          </a>
        </li>
      </ul>
    </div>
  </div>
</nav>

      {/* Modals */}
      {showModal === 'template' && (
        <div className="modal-backdrop-custom" onClick={closeModal}>
          <div className="modal-content-custom" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header-custom">
              <h3 className="modal-title-custom">Sample Resumes</h3>
              <button className="close-button" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body-custom">
              <p>Here are a few sample resume layouts for inspiration. Click on an image to view it.</p>
              <div className="resume-gallery mt-4">
                {resumeSamples.map(sample => (
                  <div key={sample.id} className="resume-item">
                     <a href={sample.src} target="_blank" rel="noopener noreferrer">
                      <img src={sample.src} alt={sample.alt} className="resume-image" />
                     </a>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}

      {showModal === 'jd-upload' && (
        <div className="modal-backdrop-custom" onClick={closeModal}>
          <div className="modal-content-custom" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header-custom">
              <h3 className="modal-title-custom">JD-Based Resume Analysis</h3>
              <button className="close-button" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body-custom">
              <p>Upload your resume and a job description to analyze how well your resume matches the job requirements.</p>
              <JDUploadForm onUploadComplete={handleJDAnalysisComplete} />
            </div>
          </div>
        </div>
      )}

      {showModal === 'instructions' && (
        <div className="modal-backdrop-custom" onClick={closeModal}>
          <div className="modal-content-custom" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header-custom">
              <h3 className="modal-title-custom">Instructions</h3>
              <button className="close-button" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body-custom">
              <h4>How to Use the Resume Analyzer</h4>
              
              <p>Follow these steps to analyze your resume:</p>
              
              <ol>
                <li><strong>Prepare Your Resume:</strong> Have your resume file ready in PDF or DOCX format.</li>
                <li><strong>Upload:</strong> Click the upload area or drag and drop your resume file.</li>
                <li><strong>Analysis:</strong> Wait a few moments while we analyze your resume content.</li>
                <li><strong>Review Results:</strong> Examine your resume score and detailed feedback.</li>
                <li><strong>Improve:</strong> Use our suggestions to enhance your resume.</li>
              </ol>
              
              <h4>JD-Based Analysis</h4>
              <p>For targeted job applications, use the JD Analysis feature to:</p>
              <ul>
                <li>Compare your resume against specific job descriptions</li>
                <li>Identify missing skills and qualifications</li>
                <li>Get a match percentage score</li>
                <li>Receive tailored improvement suggestions</li>
              </ul>
              
              <h4>What We Analyze</h4>
              <ul>
                <li>Content completeness and structure</li>
                <li>Keyword optimization for your industry</li>
                <li>Impact of your achievements and experience</li>
                <li>Formatting and readability</li>
                <li>Overall effectiveness for job applications</li>
              </ul>
            </div>
          </div>
        </div>
      )}

      <div className="app-container" style={{ marginTop: '56px' }}>
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-12 col-lg-8">
              <div className="text-center mb-5">
                <h1 className="app-title mb-2 animate-on-load">
                  {result ? "Resume Report" : "Resume Analyzer"}
                </h1>
                <p className="app-subtitle animate-on-load delay-1">
                  {result
                    ? analysisMode === "jd" 
                      ? "JD Match Analysis Results" 
                      : "Here are the detailed insights and suggestions for your resume."
                    : "Analyze your resume's quality, get a score, and receive improvement tips."}
                </p>
              </div>

              {!result ? (
                <>
                  <div className="mode-selector mb-4">
                    <button 
                      className={`mode-button ${analysisMode === "resume" ? "active" : ""}`}
                      onClick={() => setAnalysisMode("resume")}
                    >
                      Standard Analysis
                    </button>
                    <button 
                      className={`mode-button ${analysisMode === "jd" ? "active" : ""}`}
                      onClick={() => setShowModal('jd-upload')}
                    >
                      JD-Based Analysis
                    </button>
                  </div>
                  <div className="p-4 content-card animate-on-load delay-1">
                    <UploadForm onUploadComplete={setResult} analysisMode={analysisMode} />
                  </div>
                </>
              ) : (
                <div id="result-container" className="animate-on-load">
                  {analysisMode === "jd" ? (
                    <JDResultCard result={result} onReset={handleReset} />
                  ) : (
                    <ResultCard result={result} onReset={handleReset} />
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
