import React, { useState } from "react";

// The bgImage import path is commented out as it is a local file.
// In a live environment, you would need to host this image and update the URL.
// import bgImage from "./background.png";

// A simple Navbar component using Tailwind CSS
const Navbar = () => {
  return (
    <nav className="fixed top-0 left-0 w-full z-50 bg-white bg-opacity-10 backdrop-blur-md shadow-lg transition-all duration-300">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
        {/* Brand/Logo Section */}
        <div className="flex-shrink-0">
          <a href="#" className="text-xl font-bold text-white hover:text-gray-200 transition-colors duration-300">
            Resume Analyzer
          </a>
        </div>

        {/* Links section - Hidden on mobile, visible on desktop */}
        <div className="hidden md:flex space-x-8">
          <a href="#" className="text-white hover:text-gray-200 transition-colors duration-300">Home</a>
          <a href="#" className="text-white hover:text-gray-200 transition-colors duration-300">About</a>
          <a href="#" className="text-white hover:text-gray-200 transition-colors duration-300">Contact</a>
        </div>
      </div>
    </nav>
  );
};

// Placeholder components to make the main App runnable
const UploadForm = ({ onUploadComplete }) => {
  // A simple placeholder form to simulate the upload process
  const handleSubmit = (e) => {
    e.preventDefault();
    const mockResult = {
      score: 85,
      sections: {
        summary: "Excellent summary, highlights key skills.",
        experience: "Well-structured, but could use more quantifiable achievements.",
        skills: "Comprehensive list of relevant skills.",
      },
    };
    onUploadComplete(mockResult);
  };
  
  return (
    <div className="bg-white p-8 rounded-xl shadow-lg w-full max-w-lg mx-auto">
      <h2 className="text-2xl font-semibold text-center mb-6">Upload Your Resume</h2>
      <form onSubmit={handleSubmit} className="flex flex-col items-center space-y-4">
        <input type="file" accept=".pdf,.doc,.docx" className="file-input file-input-bordered w-full" />
        <button type="submit" className="w-full bg-blue-600 text-white py-2 px-4 rounded-full hover:bg-blue-700 transition-colors duration-300">
          Analyze
        </button>
      </form>
    </div>
  );
};

const ResultCard = ({ result, onReset }) => {
  return (
    <div className="bg-white p-8 rounded-xl shadow-lg w-full max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Your Report</h2>
      <div className="text-center mb-6">
        <h3 className="text-xl font-semibold text-gray-600">Overall Score:</h3>
        <p className="text-6xl font-extrabold text-blue-600 my-2">{result.score}%</p>
      </div>
      <div className="space-y-4 text-gray-700">
        {Object.entries(result.sections).map(([section, feedback], index) => (
          <div key={index} className="bg-gray-100 p-4 rounded-lg">
            <h4 className="font-bold capitalize">{section}:</h4>
            <p>{feedback}</p>
          </div>
        ))}
      </div>
      <div className="flex justify-center mt-8">
        <button onClick={onReset} className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-full hover:bg-blue-700 transition-colors duration-300">
          Analyze Another
        </button>
      </div>
    </div>
  );
};

// Main App component
export default function App() {
  const [result, setResult] = useState(null);

  const handleReset = () => {
    setResult(null);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="flex flex-col min-h-screen font-sans antialiased text-gray-800"
      style={{
        // A gradient fallback for the background
        backgroundImage: "linear-gradient(to bottom right, #2c3e50, #34495e)",
        // To use the local image, uncomment the line below and ensure the path is correct
        // backgroundImage: `url(${bgImage}), linear-gradient(to bottom right, #2c3e50, #34495e)`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
      }}
    >
      {/* 1. The new Navbar component is placed at the top of the app */}
      <Navbar />

      <main className="flex-grow flex flex-col items-center justify-center p-6 pt-24 md:pt-32">
        <div className="container mx-auto">
          <div className="w-full max-w-3xl mx-auto text-center mb-8">
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-extrabold text-white mb-4 animate-fadeInUp">
              {result ? "Resume Report" : "Resume Analyzer"}
            </h1>
            <p className="text-lg md:text-xl text-gray-200 max-w-xl mx-auto animate-fadeInUp delay-1">
              {result
                ? "Here are the detailed insights and suggestions for your resume."
                : "Analyze your resume's quality, get a score, and receive improvement tips."}
            </p>
          </div>

          {!result ? (
            <div className="animate-fadeInUp delay-2">
              <UploadForm onUploadComplete={setResult} />
            </div>
          ) : (
            <div id="result-container" className="animate-fadeInUp delay-2">
              <ResultCard result={result} onReset={handleReset} />
            </div>
          )}
        </div>
      </main>

      {/* --- CUSTOM CSS FOR ANIMATIONS --- */}
      <style>
        {`
          @keyframes fadeInUp {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }
          .animate-fadeInUp {
            animation: fadeInUp 0.8s ease-in-out forwards;
            opacity: 0;
          }
          .animate-fadeInUp.delay-1 { animation-delay: 0.2s; }
          .animate-fadeInUp.delay-2 { animation-delay: 0.4s; }
        `}
      </style>
    </div>
  );
}
