import React from "react";

export default function JDResultCard({ result, onReset }) {
  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 60) return "text-warning";
    return "text-danger";
  };

  const jdAnalysis = result.jd_analysis || {};
  const jobInfo = result.job_description || {};
  
  const formatSkills = (skills) => {
    if (!skills || !Array.isArray(skills)) return [];
    return skills.slice(0, 15);
  };

  return (
    <div className="result-card-container">
      <div className="container" style={{ maxWidth: '800px' }}>
        {/* Job Analysis Header */}
        {jobInfo.job_title && jobInfo.job_title !== "Unknown Position" && (
          <div className="card shadow-lg mb-4">
            <div className="card-body text-center p-4">
              <h4 className="card-title fw-bold mb-2">Job Match Analysis</h4>
              <p className="text-muted mb-0">
                For: <strong>{jobInfo.job_title}</strong>
                {jobInfo.required_experience > 0 && (
                  <span> | Experience Required: <strong>{jobInfo.required_experience}+ years</strong></span>
                )}
              </p>
            </div>
          </div>
        )}

        {/* Overall Score */}
        <div className="card shadow-lg mb-4">
          <div className="card-body text-center p-4">
            <h4 className="card-title fw-bold mb-3">Overall Match Score</h4>
            <div
              className={`mx-auto d-flex align-items-center justify-content-center rounded-circle ${getScoreColor(jdAnalysis.overall_score)}`}
              style={{
                width: "120px",
                height: "120px",
                background: "linear-gradient(135deg, #2c3e50, #3498db)",
                margin: "0 auto"
              }}
            >
              <h2 className="fw-bold mb-0 text-white">{jdAnalysis.overall_score}/100</h2>
            </div>
          </div>
        </div>

        {/* Skills Analysis */}
        <div className="row">
          <div className="col-md-6 mb-4">
            <div className="card shadow-sm h-100">
              <div className="card-body">
                <h5 className="card-title fw-bold text-success">
                  ✅ Matching Skills ({jdAnalysis.matching_skills?.length || 0})
                </h5>
                <div className="d-flex flex-wrap gap-2">
                  {formatSkills(jdAnalysis.matching_skills || []).map((skill, index) => (
                    <span key={index} className="badge bg-success">
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          </div>

          <div className="col-md-6 mb-4">
            <div className="card shadow-sm h-100">
              <div className="card-body">
                <h5 className="card-title fw-bold text-danger">
                  ❌ Skills to Improve ({jdAnalysis.missing_skills?.length || 0})
                </h5>
                <div className="d-flex flex-wrap gap-2">
                  {formatSkills(jdAnalysis.missing_skills || []).map((skill, index) => (
                    <span key={index} className="badge bg-danger">
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Detailed Breakdown */}
        <div className="card shadow-sm mb-4">
          <div className="card-body">
            <h5 className="card-title fw-bold mb-4">Detailed Breakdown</h5>
            
            {[
              { label: "Skills Match", value: jdAnalysis.skill_match, color: "success" },
              { label: "Experience Match", value: jdAnalysis.experience_match, color: "info" },
              { label: "Education Match", value: jdAnalysis.education_match, color: "warning" },
              { label: "Keyword Relevance", value: jdAnalysis.keyword_match, color: "primary" }
            ].map((item, index) => (
              <div key={index} className="mb-3">
                <div className="d-flex justify-content-between align-items-center mb-1">
                  <span>{item.label}</span>
                  <span className="fw-bold">{item.value}%</span>
                </div>
                <div className="progress" style={{ height: "20px" }}>
                  <div 
                    className={`progress-bar bg-${item.color}`}
                    style={{ width: `${item.value}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Action Button */}
        <div className="text-center mt-4">
          <button className="btn btn-primary" onClick={onReset}>
            Analyze Another Resume
          </button>
        </div>
      </div>
    </div>
  );
}
