import React, { useState } from "react";
import { Link } from "react-router-dom";   // ✅ Router link
import UploadForm from "./components/UploadForm";
import ResultCard from "./components/ResultCard";
import bgImage from "./background.png";

export default function App() {
  const [result, setResult] = useState(null);

  const handleReset = () => {
    setResult(null);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <>
      <style>
        {`
          body { background-color: #2c3e50; }
          .app-container {
            min-height: 100vh;
            display: flex; flex-direction: column;
            align-items: center; justify-content: flex-start;
            padding: 3rem 1rem;
            background-image: url(${bgImage});
            background-size: cover; background-position: center;
            background-repeat: no-repeat; background-attachment: fixed;
          }
          .navbar {
            width: 100%; background: rgba(0,0,0,0.6);
            padding: 1rem 2rem; position: fixed; top: 0; left: 0;
            z-index: 1000; display: flex; justify-content: flex-end; gap: 2rem;
          }
          .navbar a { color: #fff; font-size: 1.2rem; text-decoration: none; font-weight: 500; transition: color .3s; }
          .navbar a:hover { color: #f1c40f; }
        `}
      </style>

      {/* ✅ Navbar with Router links */}
      <div className="navbar">
        <Link to="/instructions">Instructions</Link>
        <Link to="/sample-template">Sample Template</Link>
      </div>

      <div className="app-container">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-12 col-lg-8">
              <div className="text-center mb-5">
                <h1 className="app-title mb-2 animate-on-load">
                  {result ? " Resume Report" : " Resume Analyzer"}
                </h1>
                <p className="app-subtitle animate-on-load delay-1">
                  {result
                    ? "Here are the detailed insights and suggestions for your resume."
                    : "Analyze your resume's quality, get a score, and receive improvement tips."}
                </p>
              </div>

              {!result ? (
                <div className="p-4 content-card animate-on-load delay-1">
                  <UploadForm onUploadComplete={setResult} />
                </div>
              ) : (
                <div id="result-container" className="animate-on-load">
                  <ResultCard result={result} onReset={handleReset} />
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
