import React from "react";

export default function JDResultCard({ result, onReset }) {
  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 60) return "text-warning";
    return "text-danger";
  };

  const jdAnalysis = result.jd_analysis || {};
  const jobInfo = result.job_description || {};
  
  // Extract position from the full JD text that's stored in job_title
  const extractPosition = (fullText) => {
    if (!fullText) return "Position Not Available";
    
    // Try to find position using multiple patterns
    const patterns = [
      /(?:position|role|title)[:\s-]*([^\n\r]+)/i,
      /(Senior|Junior|Lead)?\s*(Java|Python|React|Full.?Stack|Front.?end|Back.?end|Software|Web)\s*(Developer|Engineer|Programmer)/i,
      /(?:looking|seeking|hiring)\s+for\s+(?:a\s+)?([^\n\r.,]+)/i,
      /^[^\n\r]{5,50}(?:developer|engineer|designer|analyst|manager)/i
    ];
    
    for (const pattern of patterns) {
      const match = fullText.match(pattern);
      if (match && match[1]) {
        return match[1].trim();
      } else if (match && match[0]) {
        return match[0].trim();
      }
    }
    
    // If no pattern matches, return first reasonable line
    const lines = fullText.split('\n');
    for (const line of lines) {
      const trimmed = line.trim();
      if (trimmed && trimmed.length > 5 && trimmed.length < 60) {
        return trimmed;
      }
    }
    
    return "Position Not Available";
  };

  // Get the position - check multiple possible sources
  const jobTitle = jdAnalysis.job_title || 
                  (jobInfo.job_title ? extractPosition(jobInfo.job_title) : "") || 
                  (jobInfo.raw_text ? extractPosition(jobInfo.raw_text) : "") || 
                  "Position Not Available";

  // Format skills for better display
  const formatSkills = (skills) => {
    if (!skills || !Array.isArray(skills)) return [];
    return skills.slice(0, 20);
  };

  return (
    <div className="result-card-container">
      <div className="container" style={{ maxWidth: '800px' }}>
        {/* DEBUG: Show what we're getting */}
        {process.env.NODE_ENV === 'development' && (
          <div style={{ display: 'none' }}>
            <p>jdAnalysis.job_title: {jdAnalysis.job_title}</p>
            <p>jobInfo.job_title: {jobInfo.job_title}</p>
            <p>jobInfo.raw_text: {jobInfo.raw_text}</p>
          </div>
        )}

        {/* Position Card - Always show */}
        <div className="card shadow-lg mb-4">
          <div className="card-body text-center p-4">
            <h4 className="card-title fw-bold mb-2">Position Applied For</h4>
            <p className="text-muted mb-0">
              <strong>{jobTitle}</strong>
            </p>
          </div>
        </div>

        {/* Score Card */}
        <div className="card shadow-lg mb-4">
          <div className="card-body text-center p-4">
            <h4 className="card-title fw-bold mb-4">Overall Match Score: {jdAnalysis.overall_score || 0}/100</h4>
            
            <div className="row">
              {[
                { label: "Skills Match", value: jdAnalysis.skill_match || 0, color: "success" },
                { label: "Experience Match", value: jdAnalysis.experience_match || 0, color: "info" },
                { label: "Education Match", value: jdAnalysis.education_match || 0, color: "warning" },
                { label: "Keyword Match", value: jdAnalysis.keyword_match || 0, color: "primary" }
              ].map((item, index) => (
                <div key={index} className="col-md-3 col-6 mb-3">
                  <h6 className="mb-2">{item.label}</h6>
                  <div
                    className={`mx-auto rounded-circle d-flex align-items-center justify-content-center ${getScoreColor(item.value)}`}
                    style={{
                      width: "70px",
                      height: "70px",
                      background: "linear-gradient(135deg, #2c3e50, #34495e)",
                      margin: "0 auto"
                    }}
                  >
                    <span className="fw-bold text-white">{item.value}%</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Rest of your component remains the same */}
        {/* Skills Analysis */}
        <div className="row">
          {/* Matching Skills */}
          {jdAnalysis.matching_skills && jdAnalysis.matching_skills.length > 0 && (
            <div className="col-md-6 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title fw-bold text-success">
                    ✅ Matching Skills ({jdAnalysis.matching_skills.length})
                  </h5>
                  <p className="text-muted mb-3">Your skills that match the job requirements:</p>
                  <div className="d-flex flex-wrap gap-2">
                    {formatSkills(jdAnalysis.matching_skills).map((skill, index) => (
                      <span key={index} className="badge bg-success fs-6">
                        {skill}
                      </span>
                    ))}
                    {jdAnalysis.matching_skills.length > 20 && (
                      <span className="badge bg-info">
                        +{jdAnalysis.matching_skills.length - 20} more
                      </span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Missing Skills */}
          {jdAnalysis.missing_skills && jdAnalysis.missing_skills.length > 0 && (
            <div className="col-md-6 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title fw-bold text-danger">
                    ❌ Missing Skills ({jdAnalysis.missing_skills.length})
                  </h5>
                  <p className="text-muted mb-3">Skills required but not found in your resume:</p>
                  <div className="d-flex flex-wrap gap-2">
                    {formatSkills(jdAnalysis.missing_skills).map((skill, index) => (
                      <span key={index} className="badge bg-danger fs-6">
                        {skill}
                      </span>
                    ))}
                    {jdAnalysis.missing_skills.length > 20 && (
                      <span className="badge bg-warning">
                        +{jdAnalysis.missing_skills.length - 20} more
                      </span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Experience & Education Summary */}
        <div className="row">
          {/* Experience Summary */}
          <div className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h6 className="fw-bold">Experience Match</h6>
                <div className="progress mb-2" style={{ height: "20px" }}>
                  <div 
                    className="progress-bar bg-info"
                    style={{ width: `${jdAnalysis.experience_match || 0}%` }}
                  ></div>
                </div>
                <small className="text-muted">
                  Your experience: <strong>{result.experience || 0} years</strong> | 
                  Required: <strong>{jobInfo.required_experience || jobInfo.experience_required || "Not specified"} years</strong>
                </small>
              </div>
            </div>
          </div>

          {/* Education Summary */}
          <div className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h6 className="fw-bold">Education Match</h6>
                <div className="progress mb-2" style={{ height: "20px" }}>
                  <div 
                    className="progress-bar bg-warning"
                    style={{ width: `${jdAnalysis.education_match || 0}%` }}
                  ></div>
                </div>
                <small className="text-muted">
                  {jdAnalysis.education_match >= 50 ? 
                    "✅ Education requirements met" : 
                    "⚠️ Review education requirements"}
                </small>
              </div>
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="text-center mt-4">
          <button className="btn btn-primary me-3" onClick={onReset}>
            Analyze Another Resume
          </button>
          <button className="btn btn-outline-secondary" onClick={() => window.print()}>
