import React, { useState } from "react";
import UploadForm from "./components/UploadForm";
import ResultCard from "./components/ResultCard";

export default function App() {
  const [result, setResult] = useState(null);

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-12 col-md-9 col-lg-7">
          <div className="text-center mb-4">
            <h1 className="fw-bold" style={{ color: "#4f46e5" }}>ðŸ“„ Resume Analyzer</h1>
            <p className="text-muted">Upload PDF or DOCX â€” get contacts, sections, layout metrics, word & page counts and a unified score.</p>
          </div>

          <div className="card-glass mb-4">
            <UploadForm onUploadComplete={(data) => setResult(data)} />
          </div>

          {result && (
            <div className="card-glass">
              <ResultCard result={result} onReset={() => setResult(null)} />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
