import React from "react";

export default function ResultCard({ result, onReset }) {
  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 50) return "text-warning";
    return "text-danger";
  };

  const copy = (txt) => navigator.clipboard.writeText(txt);

  return (
    <>
      {/* Score Section */}
<div className="card shadow-lg border-0 mb-4 rounded-3">
  <div className="card-body text-center">
    <h4 className="card-title mb-3">Resume Score</h4>

    {/* Big Number */}
    <h1
      className={`fw-bold display-4 mb-3 ${getScoreColor(result.score)}`}
      style={{ fontSize: "3rem" }}
    >
      {result.score}/100
    </h1>

    {/* Progress Bar */}
    <div className="progress" style={{ height: "20px", borderRadius: "10px" }}>
      <div
        className={`progress-bar ${getScoreColor(result.score)}`}
        role="progressbar"
        style={{ width: `${result.score}%` }}
        aria-valuenow={result.score}
        aria-valuemin="0"
        aria-valuemax="100"
      >
        {result.score}%
      </div>
    </div>

    {/* Feedback Text */}
    <p className="mt-3">
      {result.score >= 80
        ? "Excellent! Your resume is strong."
        : result.score >= 50
        ? "Good. Some improvements can be made."
        : "Needs improvement. Try adding more details."}
    </p>
  </div>
</div>


      {/* Metrics */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Formatting & Layout Metrics</h5>
          <ul className="list-group">
            <li className="list-group-item">
              Average Font Size: <b>{result.metrics?.avg_font_size ?? "N/A"}</b>
            </li>
            <li className="list-group-item">
              Line Spacing (heuristic/DOCX):{" "}
              <b>{result.metrics?.avg_line_spacing ?? "N/A"}</b>
            </li>
            <li className="list-group-item">
              Indentation Count: <b>{result.metrics?.indentations ?? 0}</b>
            </li>
            <li className="list-group-item">
              Blank Lines: <b>{result.metrics?.blank_lines ?? 0}</b>
            </li>
            <li className="list-group-item">
              Total Pages: <b>{result.metrics?.pages ?? "N/A"}</b>
            </li>
            <li className="list-group-item">
              Total Words: <b>{result.metrics?.words ?? "N/A"}</b>
            </li>
          </ul>
        </div>
      </div>

      {/* Contacts */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Contact Information</h5>
          <ul className="list-group">
            {["email", "phone", "linkedin", "github"].map((k) => {
              const val = result.contacts?.[k];
              return (
                <li
                  className="list-group-item d-flex justify-content-between align-items-center"
                  key={k}
                >
                  <span>
                    {k === "email" && "📧 Email:"}
                    {k === "phone" && "📞 Phone:"}
                    {k === "linkedin" && "🔗 LinkedIn:"}
                    {k === "github" && "🐙 GitHub:"}
                    &nbsp;<b>{val || "Not found"}</b>
                  </span>
                  {val && (
                    <button
                      className="btn btn-sm btn-outline-secondary"
                      onClick={() => copy(val)}
                    >
                      Copy
                    </button>
                  )}
                </li>
              );
            })}
          </ul>
        </div>
      </div>

      {/* Sections */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Sections Found</h5>
          <ul className="list-group">
            {result.metrics?.sections_found &&
              Object.entries(result.metrics.sections_found).map(
                ([section, present]) => (
                  <li
                    key={section}
                    className="list-group-item d-flex justify-content-between"
                  >
                    {section.charAt(0).toUpperCase() + section.slice(1)}
                    <span
                      className={`badge ${
                        present ? "bg-success" : "bg-danger"
                      }`}
                    >
                      {present ? "Present" : "Missing"}
                    </span>
                  </li>
                )
              )}
          </ul>
        </div>
      </div>

      {/* Suggestions */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Suggestions</h5>
          {result.suggestions?.length ? (
            <ul className="list-group">
              {result.suggestions.map((s, i) => (
                <li key={i} className="list-group-item">
                  ⚠️ {s}
                </li>
              ))}
            </ul>
          ) : (
            <div className="text-success">✅ Your resume looks great!</div>
          )}
        </div>
      </div>

      {/* Reset Button */}
      <div className="text-center">
        <button className="btn btn-secondary" onClick={onReset}>
          Upload Another Resume
        </button>
      </div>
    </>
  );
}
