import React from "react";

export default function JDResultCard({ result, onReset }) {
  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 60) return "text-warning";
    return "text-danger";
  };

  const jdAnalysis = result.jd_analysis || {};
  
  return (
    <div className="result-card-container">
      <div className="container" style={{ maxWidth: '800px' }}>
        {/* JD Analysis Header */}
        {result.job_description && (
          <div className="card shadow-lg mb-4">
            <div className="card-body text-center p-4">
              <h4 className="card-title fw-bold mb-2">Job Description Analysis</h4>
              <p className="text-muted mb-3">
                Analysis for: <strong>{result.job_description.job_title || "Unknown Position"}</strong>
              </p>
            </div>
          </div>
        )}

        {/* Score Card */}
        <div className="card shadow-lg mb-4">
          <div className="card-body text-center p-4">
            <h4 className="card-title fw-bold mb-4">Analysis Scores</h4>
            <div className="row">
              <div className="col-md-6 mb-3 mb-md-0">
                <h5 className="mb-3">Resume Quality</h5>
                <div
                  className={`mx-auto d-flex align-items-center justify-content-center rounded-circle shadow ${getScoreColor(result.score)}`}
                  style={{ 
                    width: "140px", 
                    height: "140px",
                    background: "linear-gradient(135deg, #000000, #858586)"
                  }}
                >
                  <h2 className="fw-bold mb-0 text-white">{result.score}/100</h2>
                </div>
              </div>
              <div className="col-md-6">
                <h5 className="mb-3">JD Match</h5>
                <div
                  className={`mx-auto d-flex align-items-center justify-content-center rounded-circle shadow ${getScoreColor(jdAnalysis.overall_score)}`}
                  style={{ 
                    width: "140px", 
                    height: "140px",
                    background: "linear-gradient(135deg, #2c3e50, #3498db)"
                  }}
                >
                  <h2 className="fw-bold mb-0 text-white">{jdAnalysis.overall_score}/100</h2>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* JD Match Breakdown */}
        {jdAnalysis.overall_score !== undefined && (
          <div className="card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold mb-4">JD Match Breakdown</h5>
              
              {[
                { label: "Skills Match", value: jdAnalysis.skill_match, color: "success" },
                { label: "Experience Match", value: jdAnalysis.experience_match, color: "info" },
                { label: "Education Match", value: jdAnalysis.education_match, color: "warning" },
                { label: "Keyword Match", value: jdAnalysis.keyword_match, color: "primary" }
              ].map((item, index) => (
                <div key={index} className="mb-3">
                  <div className="d-flex justify-content-between align-items-center mb-1">
                    <span>{item.label}</span>
                    <span className="fw-bold">{item.value}%</span>
                  </div>
                  <div className="progress" style={{ height: "20px" }}>
                    <div 
                      className={`progress-bar bg-${item.color}`}
                      style={{ width: `${item.value}%` }}
                    ></div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Missing Skills */}
        {jdAnalysis.missing_skills && jdAnalysis.missing_skills.length > 0 && (
          <div className="card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold text-danger">Missing Skills</h5>
              <p className="text-muted mb-3">These skills are required but not found in your resume:</p>
              <div className="d-flex flex-wrap gap-2">
                {jdAnalysis.missing_skills.map((skill, index) => (
                  <span key={index} className="badge bg-danger">
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Matching Skills */}
        {jdAnalysis.matching_skills && jdAnalysis.matching_skills.length > 0 && (
          <div className="card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold text-success">Matching Skills</h5>
              <p className="text-muted mb-3">These skills match the job requirements:</p>
              <div className="d-flex flex-wrap gap-2">
                {jdAnalysis.matching_skills.map((skill, index) => (
                  <span key={index} className="badge bg-success">
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Reset Button */}
        <div className="text-center mt-4">
          <button className="btn btn-secondary" onClick={onReset}>
            Analyze Another
          </button>
        </div>
      </div>
    </div>
  );
}
