import React, { useState } from "react";
import UploadForm from "./components/UploadForm";
import ResultCard from "./components/ResultCard";
import bgImage from "./background.png"; // local background

export default function App() {
  const [result, setResult] = useState(null);

  const handleReset = () => {
    setResult(null);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <>
      <style>
        {`
          body { background-color: #2c3e50; }

          .app-container {
            min-height: 100vh;
            display: flex; flex-direction: column;
            align-items: center; justify-content: flex-start;
            padding: 3rem 1rem;
            background-image: url(${bgImage});
            background-size: cover; background-position: center;
            background-repeat: no-repeat; background-attachment: fixed;
          }

          .navbar {
            width: 100%;
            background: rgba(0, 0, 0, 0.6);
            padding: 1rem 2rem;
            position: fixed;
            top: 0; left: 0;
            z-index: 1000;
            display: flex;
            justify-content: flex-end;
            gap: 2rem;
          }

          .navbar a {
            color: #ffffff;
            font-size: 1.2rem;
            text-decoration: none;
            font-weight: 500;
            transition: color 0.3s ease;
          }

          .navbar a:hover {
            color: #f1c40f;
          }

          .content-card {
            background: linear-gradient(135deg, rgba(255, 255, 255, 0.8) 0%, rgba(240, 244, 248, 0.1) 100%);
            border-radius: 1.5rem;
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
            transition: all 0.3s ease;
          }

          .app-title {
            font-size: 4.5rem;
            font-weight: 700;
            color: #e0e0e0;
            text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
            transition: opacity 0.5s ease;
          }

          .app-subtitle {
            font-size: 1.5rem;
            color: #bbbbbb;
            text-shadow: 1px 1px 2px rgba(0,0,0,0.4);
            transition: opacity 0.5s ease;
          }

          @keyframes fadeInUp {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }

          .animate-on-load {
            animation: fadeInUp 0.8s ease-in-out forwards;
          }

          .animate-on-load.delay-1 {
            animation-delay: 0.2s;
          }

          @media (max-width: 768px) {
            .app-container { padding: 4rem 1rem 2rem; }
            .app-title { font-size: 2rem; }
          }
        `}
      </style>

      {/* ✅ Navbar (anchors) */}
      <div className="navbar">
        <a href="#instructions">Instructions</a>
        <a href="#sample-template">Sample Template</a>
      </div>

      <div className="app-container">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-12 col-lg-8">
              <div className="text-center mb-5">
                <h1 className="app-title mb-2 animate-on-load">
                  {result ? " Resume Report" : " Resume Analyzer"}
                </h1>
                <p className="app-subtitle animate-on-load delay-1">
                  {result
                    ? "Here are the detailed insights and suggestions for your resume."
                    : "Analyze your resume's quality, get a score, and receive improvement tips."}
                </p>
              </div>

              {!result ? (
                <div className="p-4 content-card animate-on-load delay-1">
                  <UploadForm onUploadComplete={setResult} />
                </div>
              ) : (
                <div id="result-container" className="animate-on-load">
                  <ResultCard result={result} onReset={handleReset} />
                </div>
              )}
            </div>
          </div>

          {/* ✅ Placeholder sections so navbar scroll works */}
          <div id="instructions" className="mt-5 p-4 content-card">
            <h2>Instructions</h2>
            <p>Here you can add step-by-step usage instructions for your Resume Analyzer.</p>
          </div>

          <div id="sample-template" className="mt-5 p-4 content-card">
            <h2>Sample Template</h2>
            <p>Here you can showcase a sample resume template or download link.</p>
          </div>
        </div>
      </div>
    </>
  );
}
