import React, { useState } from "react";
import UploadForm from "./components/UploadForm";
import ResultCard from "./components/ResultCard";
// ✅ Import your local background image
import bgImage from "./background.png"; // replace with your actual file name

export default function App() {
  const [result, setResult] = useState(null);
  const [showModal, setShowModal] = useState(null); // 'template' or 'instructions'

  const handleReset = () => {
    setResult(null);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const closeModal = () => {
    setShowModal(null);
  };

  return (
    <>
      <style>
        {`
          

          .app-container {
            min-height: 100vh;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: flex-start;
            

            /* --- BACKGROUND STYLES --- */
            background-image: url(${bgImage});
            background-size: 100% 100%;
            background-position: center center;
            background-repeat: no-repeat;
            background-attachment: fixed;
          }

          .content-card {
            background: linear-gradient(135deg, rgba(255, 255, 255, 0.8) 0%, rgba(240, 244, 248, 0.1) 100%);
            border-radius: 1.5rem;
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
            transition: all 0.3s ease;
          }

          .app-title {
            font-size: 4.5rem;
            font-weight: 700;
            color: #e0e0e0;
            text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
            transition: opacity 0.5s ease;
          }

          .app-subtitle {
            font-size: 1.5rem;
            color: #bbbbbb;
            text-shadow: 1px 1px 2px rgba(0,0,0,0.4);
            transition: opacity 0.5s ease;
          }

          @keyframes fadeInUp {
            from {
              opacity: 0;
              transform: translateY(20px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .animate-on-load {
            animation: fadeInUp 0.8s ease-in-out forwards;
          }

          .animate-on-load.delay-1 {
            animation-delay: 0.2s;
          }

          /* Navbar Styles */
          .navbar-custom {
            background: rgba(0, 0, 0, 0.4);
            backdrop-filter: blur(10px);
            /* box-shadow: 0 2px 15px rgba(0, 0, 0, 0.2); */
            width: 100%;
            position: fixed;
            top: 0;
            z-index: 1000;
          }
          
          .navbar-brand {
            color: #e0e0e0 !important;
            font-weight: 600;
            font-size: 1.2rem;
          }
          
          .nav-link-custom {
            color: #bbbbbb !important;
            padding: 0.5rem 1rem !important;
            border-radius: 0.5rem;
            transition: all 0.3s ease;
            margin: 0 0.2rem;
          }
          
          .nav-link-custom:hover {
            color: #ffffff !important;
            background: rgba(255, 255, 255, 0.1);
          }
          
          /* Modal Styles */
          .modal-backdrop-custom {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.7);
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 2000;
          }
          
          .modal-content-custom {
            background: linear-gradient(135deg, rgba(44, 62, 80, 0.95) 0%, rgba(32, 44, 59, 0.98) 100%);
            border-radius: 1rem;
            padding: 2rem;
            max-width: 90%;
            width: 600px;
            max-height: 80vh;
            overflow-y: auto;
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.4);
            border: 1px solid rgba(255, 255, 255, 0.1);
          }
          
          .modal-header-custom {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 1.5rem;
            padding-bottom: 1rem;
            border-bottom: 1px solid rgba(255, 255, 255, 0.1);
          }
          
          .modal-title-custom {
            color: #e0e0e0;
            font-size: 1.8rem;
            font-weight: 600;
          }
          
          .close-button {
            background: none;
            border: none;
            color: #bbbbbb;
            font-size: 1.5rem;
            cursor: pointer;
            transition: color 0.3s ease;
          }
          
          .close-button:hover {
            color: #ffffff;
          }
          
          .modal-body-custom {
            color: #d0d0d0;
            line-height: 1.6;
          }
          
          .modal-body-custom h4 {
            color: #e0e0e0;
            margin-top: 1.5rem;
            margin-bottom: 0.5rem;
          }
          
          .modal-body-custom ul {
            padding-left: 1.5rem;
          }
          
          .modal-body-custom li {
            margin-bottom: 0.5rem;
          }

          @media (max-width: 768px) {
            .app-container {
              padding: 2rem 1rem;
            }
            .app-title {
              font-size: 2rem;
            }
            
            .navbar-brand {
              font-size: 1.2rem;
            }
            
            .nav-link-custom {
              padding: 0.4rem 0.8rem !important;
              font-size: 0.9rem;
            }
            
            .modal-content-custom {
              width: 95%;
              padding: 1.5rem;
            }
            
            .modal-title-custom {
              font-size: 1.5rem;
            }
          }
        `}
      </style>

      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-custom">
        <div className="container">
          <a className="navbar-brand" href="#">
            Home
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <a 
                  className="nav-link nav-link-custom" 
                  href="#" 
                  onClick={(e) => {
                    e.preventDefault();
                    setShowModal('template');
                  }}
                >
                  Sample Template
                </a>
              </li>
              <li className="nav-item">
                <a 
                  className="nav-link nav-link-custom" 
                  href="#" 
                  onClick={(e) => {
                    e.preventDefault();
                    setShowModal('instructions');
                  }}
                >
                  Instructions
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Modals */}
      {showModal === 'template' && (
        <div className="modal-backdrop-custom" onClick={closeModal}>
          <div className="modal-content-custom" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header-custom">
              <h3 className="modal-title-custom">Sample Resume Template</h3>
              <button className="close-button" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body-custom">
              <p>For best results with our Resume Analyzer, use a well-structured resume with these sections:</p>
              
              <h4>1. Contact Information</h4>
              <p>Full name, phone number, email address, and LinkedIn profile (optional).</p>
              
              <h4>2. Professional Summary</h4>
              <p>2-3 sentences highlighting your experience, skills, and career goals.</p>
              
              <h4>3. Work Experience</h4>
              <p>List your roles in reverse chronological order with:</p>
              <ul>
                <li>Company name and location</li>
                <li>Job title</li>
                <li>Employment dates</li>
                <li>3-5 bullet points describing achievements and responsibilities</li>
              </ul>
              
              <h4>4. Education</h4>
              <p>Degree, institution name, location, and graduation year.</p>
              
              <h4>5. Skills Section</h4>
              <p>List relevant technical and soft skills.</p>
              
              <h4>Tips for Best Results:</h4>
              <ul>
                <li>Use clear section headings</li>
                <li>Include measurable achievements</li>
                <li>Keep formatting clean and consistent</li>
                <li>Save your resume as a PDF before uploading</li>
              </ul>
            </div>
          </div>
        </div>
      )}

      {showModal === 'instructions' && (
        <div className="modal-backdrop-custom" onClick={closeModal}>
          <div className="modal-content-custom" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header-custom">
              <h3 className="modal-title-custom">Instructions</h3>
              <button className="close-button" onClick={closeModal}>×</button>
            </div>
            <div className="modal-body-custom">
              <h4>How to Use the Resume Analyzer</h4>
              
              <p>Follow these steps to analyze your resume:</p>
              
              <ol>
                <li><strong>Prepare Your Resume:</strong> Have your resume file ready in PDF, DOC, or DOCX format.</li>
                <li><strong>Upload:</strong> Click the upload area or drag and drop your resume file.</li>
                <li><strong>Analysis:</strong> Wait a few moments while we analyze your resume content.</li>
                <li><strong>Review Results:</strong> Examine your resume score and detailed feedback.</li>
                <li><strong>Improve:</strong> Use our suggestions to enhance your resume.</li>
              </ol>
              
              <h4>What We Analyze</h4>
              <ul>
                <li>Content completeness and structure</li>
                <li>Keyword optimization for your industry</li>
                <li>Impact of your achievements and experience</li>
                <li>Formatting and readability</li>
                <li>Overall effectiveness for job applications</li>
              </ul>
              
              <h4>Tips for Best Results</h4>
              <ul>
                <li>Ensure your resume is up-to-date</li>
                <li>Include quantifiable achievements</li>
                <li>Use industry-relevant keywords</li>
                <li>Check for spelling and grammar errors before uploading</li>
              </ul>
            </div>
          </div>
        </div>
      )}

      <div className="app-container" style={{ marginTop: '76px' }}>
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-12 col-lg-8">
              <div className="text-center mb-5">
                <h1 className="app-title mb-2 animate-on-load">
                  {result ? " Resume Report" : " Resume Analyzer"}
                </h1>
                <p className="app-subtitle animate-on-load delay-1">
                  {result
                    ? "Here are the detailed insights and suggestions for your resume."
                    : "Analyze your resume's quality, get a score, and receive improvement tips."}
                </p>
              </div>

              {!result ? (
                <div className="p-4 content-card animate-on-load delay-1">
                  <UploadForm onUploadComplete={setResult} />
                </div>
              ) : (
                <div id="result-container" className="animate-on-load">
                  <ResultCard result={result} onReset={handleReset} />
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
