import React, { useState } from "react";

export default function ResultCard({ result, onReset }) {
  // State to give feedback when text is copied
  const [copiedText, setCopiedText] = useState(null);

  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 50) return "text-warning";
    return "text-danger";
  };
  
  // CHANGE 1: Upgraded copy-to-clipboard function for better reliability and UX
  const copyToClipboard = (textToCopy, type) => {
    navigator.clipboard.writeText(textToCopy).then(() => {
      setCopiedText(type);
      setTimeout(() => setCopiedText(null), 2000); // Reset after 2 seconds
    }, (err) => {
      console.error('Failed to copy!', err);
    });
  };

  // Helper to render keyword lists
  const renderKeywords = (keywords, type) => {
    if (!keywords || keywords.length === 0) {
      return <p className="text-muted">None found.</p>;
    }
    return (
      <div className="d-flex flex-wrap gap-2">
        {keywords.map((keyword, index) => (
          <span key={index} className={`badge ${type === 'matched' ? 'bg-success-soft' : 'bg-danger-soft'}`}>
            {keyword}
          </span>
        ))}
      </div>
    );
  };

  return (
    <>
      <style>
        {`
        .result-card-container {
            background: linear-gradient(135deg, rgba(255, 255, 255, 0.2) 0%, rgba(240, 244, 248, 0.1) 100%);
            padding: 3rem 1rem;
            display: flex;
            flex-direction: column;
            align-items: center;
            border-radius: 1rem;
            justify-content: center;
            backdrop-filter: blur(10px);
            -webkit-backdrop-filter: blur(10px);
        }
        .custom-card {
            background: rgba(255, 255, 255, 0.85);
            backdrop-filter: blur(2px);
            border-radius: 1rem !important;
            border: none;
        }
        .score-circle {
            background: linear-gradient(135deg, #000000ff, #858586ff);
            transition: all 0.3s ease;
        }
        .score-circle h1 {
            color: #ffffffff;
            text-shadow: 1px 1px 2px rgba(0,0,0,0.1);
        }
        .list-group-item {
            background-color: transparent;
            border-color: rgba(0, 0, 0, 0.1);
        }
        /* CHANGE 3: Added styles for keyword badges */
        .bg-success-soft {
            background-color: rgba(25, 135, 84, 0.2);
            color: #0f5132;
            font-weight: 500;
        }
        .bg-danger-soft {
            background-color: rgba(220, 53, 69, 0.2);
            color: #842029;
            font-weight: 500;
        }
        `}
      </style>
      <div className="result-card-container">
        <div className="container" style={{ maxWidth: '600px' }}>
          {/* Main Score */}
          <div className="card custom-card shadow-lg mb-4">
            <div className="card-body text-center p-4">
              <h4 className="card-title fw-bold mb-3">Overall Resume Score</h4>
              <div
                className={`mx-auto d-flex align-items-center justify-content-center rounded-circle shadow score-circle ${getScoreColor(
                  // CHANGE 2: Using 'overall_score' to match new API response
                  result.overall_score 
                )}`}
                style={{ width: "160px", height: "160px" }}
              >
                <h1 className="fw-bold mb-0">{result.overall_score}/100</h1>
              </div>
            </div>
          </div>

          {/* CHANGE 4: Conditionally render the new Job Match Analysis card */}
          {result.job_match_analysis && (
            <div className="card custom-card shadow-lg mb-4">
                <div className="card-body p-4">
                    <h4 className="card-title fw-bold text-center mb-3">Job Description Match</h4>
                    <div className="text-center mb-4">
                        <h2 className={`fw-bold ${getScoreColor(result.job_match_analysis.match_score)}`}>
                            {result.job_match_analysis.match_score}%
                        </h2>
                        <p className="text-muted mb-0">Match Score</p>
                    </div>

                    <div className="mb-3">
                        <h6 className="fw-bold">✅ Matched Keywords</h6>
                        {renderKeywords(result.job_match_analysis.matched_keywords, 'matched')}
                    </div>
                    
                    <div className="mb-3">
                        <h6 className="fw-bold">❌ Missing Keywords</h6>
                        {renderKeywords(result.job_match_analysis.missing_keywords, 'missing')}
                    </div>

                    {result.job_match_analysis.feedback && (
                       <div>
                         <h6 className="fw-bold">💡 Feedback</h6>
                         <p className="mb-0">{result.job_match_analysis.feedback}</p>
                       </div>
                    )}
                </div>
            </div>
          )}

          {/* Metrics */}
          <div className="card custom-card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold">Formatting & Layout Metrics</h5>
              {/* This section remains the same */}
               <ul className="list-group list-group-flush">
                <li className="list-group-item">
                  Average Font Size: <b>{result.metrics?.avg_font_size ?? "N/A"}</b>
                </li>
                <li className="list-group-item">
                  Line Spacing (heuristic/DOCX): <b>{result.metrics?.avg_line_spacing ?? "N/A"}</b>
                </li>
                <li className="list-group-item">
                  Indentation Count: <b>{result.metrics?.indentations ?? 0}</b>
                </li>
                <li className="list-group-item">
                  Blank Lines: <b>{result.metrics?.blank_lines ?? 0}</b>
                </li>
                <li className="list-group-item">
                  Total Pages: <b>{result.metrics?.pages ?? "N/A"}</b>
                </li>
                <li className="list-group-item">
                  Total Words: <b>{result.metrics?.words ?? "N/A"}</b>
                </li>
              </ul>
            </div>
          </div>

          {/* Contacts */}
          <div className="card custom-card shadow-sm mb-4">
            <div className="card-body">
              <h5 className="card-title fw-bold">Contact Information</h5>
               <ul className="list-group list-group-flush">
                 {["email", "phone", "linkedin", "github"].map((k) => {
                   const val = result.contacts?.[k];
                   return (
                     <li className="list-group-item d-flex justify-content-between align-items-center" key={k}>
                       <span>
                         {k === "email" && "📧 Email:"}
                         {k === "phone" && "📞 Phone:"}
                         {k === "linkedin" && "🔗 LinkedIn:"}
                         {k === "github" && "🐙 GitHub:"}
                         &nbsp;<b>{val || "Not found"}</b>
                       </span>
                       {val && (
                         <button
                           className="btn btn-sm btn-outline-secondary"
                           onClick={() => copyToClipboard(val, k)}
                         >
                           {copiedText === k ? "Copied!" : "Copy"}
                         </button>
                       )}
                     </li>
                   );
                 })}
               </ul>
            </div>
          </div>
          
          {/* Other sections like 'Sections Found' and 'Suggestions' remain unchanged */}

          {/* Reset Button */}
          <div className="text-center mt-4">
            <button className="btn btn-secondary" onClick={onReset}>
              Upload Another Resume
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
