import React from "react";

export default function ResultCard({ result, onReset }) {
  const getScoreColor = (score) => {
    if (score >= 80) return "#28a745"; // green
    if (score >= 50) return "#ffc107"; // yellow
    return "#dc3545"; // red
  };

  const copy = (txt) => navigator.clipboard.writeText(txt);

  return (
    <>
      {/* Score */}
      <div className="card shadow-lg border-0 mb-4 rounded-4">
        <div className="card-body text-center p-4">
          <h4 className="card-title fw-bold mb-3">Resume Score</h4>

          <div
            className="mx-auto position-relative"
            style={{ width: "140px", height: "140px" }}
          >
            {/* Background Circle */}
            <svg width="140" height="140" className="position-absolute">
              <circle
                cx="70"
                cy="70"
                r="60"
                stroke="#e9ecef"
                strokeWidth="12"
                fill="none"
              />
              <circle
                cx="70"
                cy="70"
                r="60"
                stroke={getScoreColor(result.score)}
                strokeWidth="12"
                fill="none"
                strokeDasharray={2 * Math.PI * 60}
                strokeDashoffset={
                  2 * Math.PI * 60 - (result.score / 100) * 2 * Math.PI * 60
                }
                style={{ transition: "stroke-dashoffset 1s ease" }}
              />
            </svg>

            {/* Center Score */}
            <div
              className="position-absolute d-flex align-items-center justify-content-center rounded-circle"
              style={{
                width: "140px",
                height: "140px",
                top: 0,
                left: 0,
                fontSize: "1.8rem",
                fontWeight: "bold",
              }}
            >
              {result.score}/100
            </div>
          </div>

          {result.metrics?.is_fresher && (
            <span className="badge bg-info mt-2">Fresher detected</span>
          )}
        </div>
      </div>

      {/* Metrics */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Formatting & Layout Metrics</h5>
          <ul className="list-group">
            <li className="list-group-item">
              Average Font Size: <b>{result.metrics?.avg_font_size ?? "N/A"}</b>
            </li>
            <li className="list-group-item">
              Line Spacing: <b>{result.metrics?.avg_line_spacing ?? "N/A"}</b>
            </li>
            <li className="list-group-item">
              Indentation Count: <b>{result.metrics?.indentations ?? 0}</b>
            </li>
            <li className="list-group-item">
              Blank Lines: <b>{result.metrics?.blank_lines ?? 0}</b>
            </li>
            <li className="list-group-item">
              Total Pages: <b>{result.metrics?.page_count ?? "N/A"}</b>
            </li>
            <li className="list-group-item">
              Total Words: <b>{result.metrics?.word_count ?? "N/A"}</b>
            </li>
          </ul>
        </div>
      </div>

      {/* Contacts */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Contact Information</h5>
          <ul className="list-group">
            {["email", "phone", "linkedin", "github"].map((k) => {
              const val = result.contacts?.[k];
              return (
                <li
                  className="list-group-item d-flex justify-content-between align-items-center"
                  key={k}
                >
                  <span>
                    {k === "email" && "📧 Email:"}
                    {k === "phone" && "📞 Phone:"}
                    {k === "linkedin" && "🔗 LinkedIn:"}
                    {k === "github" && "🐙 GitHub:"}
                    &nbsp;<b>{val || "Not found"}</b>
                  </span>
                  {val && (
                    <button
                      className="btn btn-sm btn-outline-secondary"
                      onClick={() => copy(val)}
                    >
                      Copy
                    </button>
                  )}
                </li>
              );
            })}
          </ul>
        </div>
      </div>

      {/* Sections */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Sections Found</h5>
          <ul className="list-group">
            {result.metrics?.sections_found &&
              Object.entries(result.metrics.sections_found).map(
                ([section, present]) => (
                  <li
                    key={section}
                    className="list-group-item d-flex justify-content-between"
                  >
                    {section.charAt(0).toUpperCase() + section.slice(1)}
                    <span
                      className={`badge ${
                        present ? "bg-success" : "bg-danger"
                      }`}
                    >
                      {present ? "Present" : "Missing"}
                    </span>
                  </li>
                )
              )}
          </ul>
        </div>
      </div>

      {/* Suggestions */}
      <div className="card shadow-sm border-0 mb-4">
        <div className="card-body">
          <h5 className="card-title">Suggestions</h5>
          {result.suggestions?.length ? (
            <ul className="list-group">
              {result.suggestions.map((s, i) => (
                <li key={i} className="list-group-item">
                  ⚠️ {s}
                </li>
              ))}
            </ul>
          ) : (
            <div className="text-success">✅ Your resume looks great!</div>
          )}
        </div>
      </div>

      {/* Reset Button */}
      <div className="text-center">
        <button className="btn btn-secondary" onClick={onReset}>
          Upload Another Resume
        </button>
      </div>
    </>
  );
}
