import React from "react";

export default function ResultCard({ result, onReset }) {
  const colorClass = (score) => (score >= 80 ? "text-success" : score >= 50 ? "text-warning" : "text-danger");
  const copy = (txt) => navigator.clipboard.writeText(txt);

  return (
    <>
      <div className="card mb-4 border-0">
        <div className="card-body text-center">
          <h4 className="card-title">Resume Score</h4>
          <h1 className={`display-5 ${colorClass(result.score)}`}>{result.score}/100</h1>
          <div className="mt-2">
            <span className="badge badge-soft">Fresher: {result.metrics?.is_fresher ? "Yes" : "No"}</span>
            <span className="ms-2 badge bg-light text-dark">Words: {result.metrics?.word_count}</span>
            <span className="ms-2 badge bg-light text-dark">Pages: {result.metrics?.page_count}</span>
          </div>
        </div>
      </div>

      {/* Metrics */}
      <div className="mb-4">
        <h5>Formatting & Layout</h5>
        <ul className="list-group">
          <li className="list-group-item">Average Font Size: <b>{result.metrics?.avg_font_size ?? "N/A"}</b></li>
          <li className="list-group-item">Average Line Spacing: <b>{result.metrics?.avg_line_spacing ?? "N/A"}</b></li>
          <li className="list-group-item">Indentation (approx): <b>{result.metrics?.indentations ?? 0}</b></li>
          <li className="list-group-item">Blank Lines: <b>{result.metrics?.blank_lines ?? 0}</b></li>
        </ul>
      </div>

      {/* Contacts */}
      <div className="mb-4">
        <h5>Contact Info</h5>
        <ul className="list-group">
          {["email", "phone", "linkedin", "github"].map(k => {
            const val = result.contacts?.[k];
            return (
              <li className="list-group-item d-flex justify-content-between align-items-center" key={k}>
                <span>
                  {k === "email" && "üìß Email:"}
                  {k === "phone" && "üìû Phone:"}
                  {k === "linkedin" && "üîó LinkedIn:"}
                  {k === "github" && "üêô GitHub:"}
                  &nbsp;<b>{val || "Not found"}</b>
                </span>
                {val && <button className="btn btn-sm btn-outline-secondary" onClick={() => copy(val)}>Copy</button>}
              </li>
            );
          })}
        </ul>
      </div>

      {/* Sections */}
      <div className="mb-4">
        <h5>Sections Found</h5>
        <ul className="list-group">
          {result.metrics?.sections_found && Object.entries(result.metrics.sections_found).map(([s, present]) => (
            <li className="list-group-item d-flex justify-content-between" key={s}>
              {s.charAt(0).toUpperCase() + s.slice(1)}
              <span className={`badge ${present ? "bg-success" : "bg-danger"}`}>{present ? "Present" : "Missing"}</span>
            </li>
          ))}
        </ul>
      </div>

      {/* Suggestions */}
      <div className="mb-4">
        <h5>Suggestions</h5>
        {result.suggestions && result.suggestions.length > 0 ? (
          <ul className="list-group">
            {result.suggestions.map((s, i) => <li key={i} className="list-group-item">‚ö† {s}</li>)}
          </ul>
        ) : <div className="text-success">‚úÖ No suggestions ‚Äî resume looks good!</div>}
      </div>

      <div className="text-center">
        <button className="btn btn-secondary" onClick={onReset}>Upload Another Resume</button>
      </div>
    </>
  );
}
