import React, { useState } from "react";
// Make sure your api service is ready to accept the job description
import { analyzeFile } from "../services/api";

export default function UploadForm({ onUploadComplete }) {
  const [file, setFile] = useState(null);
  // 1. ADD STATE FOR THE JOB DESCRIPTION
  const [jobDescription, setJobDescription] = useState("");
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");
  const [isDragOver, setIsDragOver] = useState(false);

  const onSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setErr("Please select a resume file (.pdf or .docx).");
      return;
    }

    const validMimeTypes = [
      "application/pdf",
      "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
    ];
    if (!validMimeTypes.includes(file.type)) {
      setErr("Only PDF or DOCX files are allowed.");
      return;
    }

    setErr("");
    setLoading(true);

    try {
      // 3. UPDATE THE API CALL to send both the file and the job description
      const result = await analyzeFile(file, jobDescription);
      onUploadComplete(result);
      // Smoothly scroll to the results section
      const resultElement = document.getElementById("result-container");
      if (resultElement) {
        resultElement.scrollIntoView({ behavior: "smooth" });
      }
    } catch (e) {
      setErr(e.message || "Failed to analyze resume.");
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    setFile(selectedFile || null);
    if (selectedFile) {
      setErr(""); // Clear error on new file selection
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const selectedFile = e.dataTransfer.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
      setErr("");
    }
  };

  return (
    <>
      <style>
        {`
        .drop-zone {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            padding: 2rem;
            border: 2px dashed #64676bff;
            border-radius: 1rem;
            text-align: center;
            cursor: pointer;
            transition: all 0.2s ease-in-out;
            background-color: rgba(98, 101, 105, 0.1);
            backdrop-filter: blur(5px);
            -webkit-backdrop-filter: blur(5px);
            position: relative;
        }
        .drop-zone:hover, .drop-zone.drag-over {
            border-color: #959598ff;
            background-color: rgba(195, 196, 200, 0.8);
        }
        .drop-zone-text {
            color: #0d0d0dff;
            font-weight: 500;
        }
        .file-name-text {
            font-weight: 600;
            color: #1f2937;
            margin-top: 0.5rem;
        }
        .btn-analyze {
            color: #000000ff;
            background-color: #ffffffff;
            border-color: #7b7b7bff;
            transition: background-color 0.2s ease;
        }
        
        .btn-analyze:hover {
            background-color: #59595bff;
        }

        /* --- STYLES FOR THE NEW TEXT AREA --- */
        .jd-label {
            color: #d1d5db;
            font-weight: 500;
            margin-bottom: 0.5rem;
            text-align: left;
            width: 100%;
        }
        .jd-textarea {
          width: 100%;
          background-color: rgba(98, 101, 105, 0.1);
          backdrop-filter: blur(5px);
          border: 1px solid #64676bff;
          color: #e0e0e0;
          border-radius: 0.75rem;
          padding: 0.75rem;
        }
        .jd-textarea::placeholder {
          color: #9ca3af;
        }
        .jd-textarea:focus {
          background-color: rgba(195, 196, 200, 0.2);
          border-color: #959598ff;
          color: #ffffff;
          outline: none;
          box-shadow: none;
        }
        `}
      </style>
      <form onSubmit={onSubmit}>
        <div
          className={`drop-zone ${isDragOver ? "drag-over" : ""}`}
          onDragOver={(e) => {
            e.preventDefault();
            setIsDragOver(true);
          }}
          onDragLeave={() => setIsDragOver(false)}
          onDrop={handleDrop}
          onClick={() => document.getElementById("file-upload").click()}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-12 w-12 text-gray-400 mb-2"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            style={{ width: "3rem", height: "3rem", color: "#5b5d60ff" }}
          >
            {/* SVG paths remain unchanged */}
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-4-4v-1a4 4 0 014-4h1a4 4 0 014 4v1a4 4 0 01-4 4h1a4 4 0 00-4-4v-1a4 4 0 00-4 4z" />
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v-1a4 4 0 014-4h1a4 4 0 014 4v1a4 4 0 01-4 4h-1a4 4 0 01-4-4z" />
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 8a4 4 0 014-4h1a4 4 0 014 4v1a4 4 0 01-4 4h-1a4 4 0 01-4-4z" />
          </svg>

          <p className="drop-zone-text">
            {file ? (
              <span className="file-name-text">{file.name}</span>
            ) : (
              "Drag & drop your resume here or click to browse"
            )}
          </p>
          <p className="text-muted text-sm">Accepted formats: .pdf, .docx</p>

          <input
            id="file-upload"
            type="file"
            className="form-control d-none"
            accept=".pdf,.docx"
            onChange={handleFileChange}
            disabled={loading}
          />
        </div>

        {/* 2. ADD THE TEXT AREA FOR THE JOB DESCRIPTION */}
        <div className="mt-4">
            <label htmlFor="jobDescription" className="jd-label">
                Paste Job Description (Optional)
            </label>
            <textarea
                id="jobDescription"
                className="jd-textarea"
                rows="8"
                placeholder="For a job match score, paste the job description here..."
                value={jobDescription}
                onChange={(e) => setJobDescription(e.target.value)}
                disabled={loading}
            ></textarea>
        </div>

        {err && <div className="alert alert-danger mt-3">{err}</div>}

        <div className="d-grid mt-4">
          <button
            className="btn btn-analyze btn-lg text-black shadow"
            type="submit"
            disabled={!file || loading}
          >
            {loading ? (
              <>
                <span
                  className="spinner-border spinner-border-sm me-2"
                  role="status"
                  aria-hidden="true"
                ></span>
                Analyzing...
              </>
            ) : (
              "Analyze My Resume"
            )}
          </button>
        </div>
      </form>
    </>
  );
}
