import React, { useState } from "react";

// The UploadForm component is defined here for self-containment
const UploadForm = ({ onUploadComplete }) => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");
  const [isDragOver, setIsDragOver] = useState(false);

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setErr("Please select a resume file (.pdf or .docx).");
      return;
    }
    const validMimeTypes = [
      "application/pdf",
      "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
    ];
    if (!validMimeTypes.includes(file.type)) {
      setErr("Only PDF or DOCX files are allowed.");
      return;
    }
    setErr("");
    setLoading(true);
    try {
      // Simulating API call for demonstration
      const result = {
        score: Math.floor(Math.random() * 100),
        metrics: {
          avg_font_size: (Math.random() * (12 - 9) + 9).toFixed(1),
          avg_line_spacing: (Math.random() * (1.5 - 1.1) + 1.1).toFixed(1),
          indentations: Math.floor(Math.random() * 5),
          blank_lines: Math.floor(Math.random() * 10),
          pages: 1,
          words: Math.floor(Math.random() * (600 - 300) + 300),
          sections_found: {
            summary: Math.random() > 0.5,
            experience: Math.random() > 0.5,
            education: Math.random() > 0.5,
            skills: Math.random() > 0.5,
            projects: Math.random() > 0.5,
          },
        },
        contacts: {
          email: "test@example.com",
          phone: "123-456-7890",
          linkedin: "https://linkedin.com/in/user",
          github: "https://github.com/user",
        },
        suggestions: ["Suggestion 1", "Suggestion 2", "Suggestion 3"],
      };
      onUploadComplete(result);
    } catch (e) {
      setErr(e.message || "Failed to analyze resume.");
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    setFile(selectedFile || null);
    if (selectedFile) {
      setErr("");
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const selectedFile = e.dataTransfer.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
      setErr("");
    }
  };

  return (
    <div className="upload-form-container">
      <form onSubmit={onSubmit}>
        <div
          className={`drop-zone ${isDragOver ? "drag-over" : ""}`}
          onDragOver={(e) => {
            e.preventDefault();
            setIsDragOver(true);
          }}
          onDragLeave={() => setIsDragOver(false)}
          onDrop={handleDrop}
          onClick={() => document.getElementById("file-upload").click()}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-12 w-12 text-gray-400 mb-2"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            style={{ width: "3rem", height: "3rem", color: "#d1d5db" }}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M7 16a4 4 0 01-4-4v-1a4 4 0 014-4h1a4 4 0 014 4v1a4 4 0 01-4 4h1a4 4 0 00-4-4v-1a4 4 0 00-4 4z"
            />
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 16v-1a4 4 0 014-4h1a4 4 0 014 4v1a4 4 0 01-4 4h-1a4 4 0 01-4-4z"
            />
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M7 8a4 4 0 014-4h1a4 4 0 014 4v1a4 4 0 01-4 4h-1a4 4 0 01-4-4z"
            />
          </svg>
          <p className="drop-zone-text">
            {file ? (
              <span className="file-name-text">{file.name}</span>
            ) : (
              "Drag & drop your resume here or click to browse"
            )}
          </p>
          <p className="text-muted text-sm">Accepted formats: .pdf, .docx</p>
          <input
            id="file-upload"
            type="file"
            className="form-control d-none"
            accept=".pdf,.docx"
            onChange={handleFileChange}
            disabled={loading}
          />
        </div>
        {err && <div className="alert alert-danger mt-3">{err}</div>}
        <div className="d-grid mt-4">
          <button
            className="btn btn-analyze btn-lg text-white shadow"
            type="submit"
            disabled={!file || loading}
          >
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                Analyzing...
              </>
            ) : (
              "Analyze My Resume"
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

// The ResultCard component is defined here
const ResultCard = ({ result, onReset }) => {
  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 50) return "text-warning";
    return "text-danger";
  };
  const copy = (txt) => document.execCommand('copy');
  return (
    <div className="result-card-container">
      <div className="container" style={{ maxWidth: '600px' }}>
        <div className="card custom-card shadow-lg mb-4">
          <div className="card-body text-center p-4">
            <h4 className="card-title fw-bold mb-3">Resume Score</h4>
            <div
              className={`mx-auto d-flex align-items-center justify-content-center rounded-circle shadow score-circle ${getScoreColor(result.score)}`}
              style={{ width: "160px", height: "160px" }}
            >
              <h1 className="fw-bold mb-0">{result.score}/100</h1>
            </div>
          </div>
        </div>
        <div className="card custom-card shadow-sm mb-4">
          <div className="card-body">
            <h5 className="card-title fw-bold">Formatting & Layout Metrics</h5>
            <ul className="list-group list-group-flush">
              <li className="list-group-item">Average Font Size: <b>{result.metrics?.avg_font_size ?? "N/A"}</b></li>
              <li className="list-group-item">Line Spacing: <b>{result.metrics?.avg_line_spacing ?? "N/A"}</b></li>
              <li className="list-group-item">Indentation Count: <b>{result.metrics?.indentations ?? 0}</b></li>
              <li className="list-group-item">Blank Lines: <b>{result.metrics?.blank_lines ?? 0}</b></li>
              <li className="list-group-item">Total Pages: <b>{result.metrics?.pages ?? "N/A"}</b></li>
              <li className="list-group-item">Total Words: <b>{result.metrics?.words ?? "N/A"}</b></li>
            </ul>
          </div>
        </div>
        <div className="card custom-card shadow-sm mb-4">
          <div className="card-body">
            <h5 className="card-title fw-bold">Contact Information</h5>
            <ul className="list-group list-group-flush">
              {["email", "phone", "linkedin", "github"].map((k) => {
                const val = result.contacts?.[k];
                return (
                  <li className="list-group-item d-flex justify-content-between align-items-center" key={k}>
                    <span>{k === "email" && "üìß Email:"}{k === "phone" && "üìû Phone:"}{k === "linkedin" && "üîó LinkedIn:"}{k === "github" && "üêô GitHub:"}&nbsp;<b>{val || "Not found"}</b></span>
                    {val && (<button className="btn btn-sm btn-outline-secondary" onClick={() => copy(val)}>Copy</button>)}
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
        <div className="card custom-card shadow-sm mb-4">
          <div className="card-body">
            <h5 className="card-title fw-bold">Suggestions</h5>
            {result.suggestions?.length ? (<ul className="list-group list-group-flush">{result.suggestions.map((s, i) => (<li key={i} className="list-group-item">‚ö†Ô∏è {s}</li>))}</ul>) : (<div className="text-success p-3"><h6 className="mb-0">‚úÖ Your resume looks great!</h6></div>)}
          </div>
        </div>
        <div className="d-flex justify-content-center mt-4">
          <button className="btn btn-secondary" onClick={onReset}>
            Analyze New Resume
          </button>
        </div>
      </div>
    </div>
  );
};


// New static content page components
const InstructionsPage = ({ onNavigate }) => (
    <div className="content-card p-5 animate-on-load">
        <h2 className="fw-bold text-center mb-4">How to Create an ATS-Friendly Resume</h2>
        <p>An ATS (Applicant Tracking System) is software used by recruiters to scan and rank resumes. Here are some tips to make your resume easily scannable:</p>
        <ul className="list-group list-group-flush mb-4">
            <li className="list-group-item"><strong>1. Use Standard Headings:</strong> Use clear, common section titles like "Work Experience," "Education," "Skills," and "Summary."</li>
            <li className="list-group-item"><strong>2. Use Standard Fonts:</strong> Stick to professional, readable fonts like Arial, Calibri, or Times New Roman.</li>
            <li className="list-group-item"><strong>3. Avoid Graphics and Tables:</strong> ATS software often struggles to read information presented in images, charts, or tables.</li>
            <li className="list-group-item"><strong>4. Include Relevant Keywords:</strong> Carefully read the job description and include key skills and buzzwords in your resume.</li>
            <li className="list-group-item"><strong>5. Use Bullet Points:</strong> Use bullet points to list your responsibilities and achievements to make them easy to read for both humans and machines.</li>
        </ul>
        <div className="text-center mt-4">
            <button className="btn btn-primary" onClick={() => onNavigate('home')}>
                Back to Analyzer
            </button>
        </div>
    </div>
);

const SampleTemplatePage = ({ onNavigate }) => (
    <div className="content-card p-5 animate-on-load">
        <h2 className="fw-bold text-center mb-4">Sample Resume Template</h2>
        <pre className="p-3 border rounded bg-light">
            {`[Your Name]
[Email] | [Phone] | [LinkedIn] | [GitHub/Portfolio]

### Summary
[A concise summary of your professional skills and experience, tailored to the job description.]

### Skills
[List of your key technical and soft skills, e.g., Python, JavaScript, React, SQL, Problem-Solving, Teamwork.]

### Work Experience
**[Job Title] | [Company Name], [City, State]**
[Start Date] ‚Äì [End Date]
- [Action verb] [your accomplishment] using [skill].
- [Action verb] [your accomplishment] using [skill].
- [Action verb] [your accomplishment] using [skill].

**[Job Title] | [Company Name], [City, State]**
[Start Date] ‚Äì [End Date]
- [Action verb] [your accomplishment] using [skill].

### Education
**[Degree] | [University Name], [City, State]**
[Graduation Year]

### Projects
**[Project Name]**
- [Brief description of the project and your role.]
- [Technologies used: e.g., Node.js, Express, MongoDB.]
- [Link to project on GitHub/live site.]`}
        </pre>
        <div className="text-center mt-4">
            <button className="btn btn-primary" onClick={() => onNavigate('home')}>
                Back to Analyzer
            </button>
        </div>
    </div>
);


// Main Navigation component
const Navbar = ({ currentView, onNavigate }) => {
    const navItemClass = (viewName) =>
        `nav-item ${currentView === viewName ? 'active' : ''}`;

    return (
        <nav className="navbar navbar-expand-lg navbar-dark fixed-top" style={{ backgroundColor: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(5px)' }}>
            <div className="container-fluid justify-content-center">
                <ul className="navbar-nav">
                    <li className={navItemClass('home')}>
                        <button className="btn btn-nav" onClick={() => onNavigate('home')}>Resume Analyzer</button>
                    </li>
                    <li className={navItemClass('instructions')}>
                        <button className="btn btn-nav" onClick={() => onNavigate('instructions')}>ATS Instructions</button>
                    </li>
                    <li className={navItemClass('template')}>
                        <button className="btn btn-nav" onClick={() => onNavigate('template')}>Sample Template</button>
                    </li>
                </ul>
            </div>
        </nav>
    );
};


// The main App component
export default function App() {
  const [result, setResult] = useState(null);
  const [view, setView] = useState('home');

  const handleReset = () => {
    setResult(null);
    setView('home');
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const renderContent = () => {
    switch(view) {
      case 'home':
        return (
          <>
            <div className="text-center mb-5">
              <h1 className="app-title mb-2 animate-on-load">
                {result ? "üìã Your Resume Report" : "üìÑ Resume Analyzer"}
              </h1>
              <p className="app-subtitle animate-on-load delay-1">
                {result
                  ? "Here are the detailed insights and suggestions for your resume."
                  : "Analyze your resume's quality, get a score, and receive improvement tips."}
              </p>
            </div>
            {!result ? (
              <div className="p-4 content-card animate-on-load delay-1">
                <UploadForm onUploadComplete={setResult} />
              </div>
            ) : (
              <div id="result-container" className="animate-on-load">
                <ResultCard result={result} onReset={handleReset} />
              </div>
            )}
          </>
        );
      case 'instructions':
        return <InstructionsPage onNavigate={setView} />;
      case 'template':
        return <SampleTemplatePage onNavigate={setView} />;
      default:
        return null;
    }
  };

  return (
    <>
      <style>
        {`
          body {
            background-color: #2c3e50;
          }
          .app-container {
            min-height: 100vh;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: flex-start;
            padding: 3rem 1rem;
            background-image: url('http://googleusercontent.com/file_content/0');
            background-size: cover;
            background-position: center center;
            background-repeat: no-repeat;
            background-attachment: fixed;
          }
          .content-card {
            background-color: rgba(255, 255, 255, 0.9);
            border-radius: 1.5rem;
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
            transition: all 0.3s ease;
          }
          .app-title {
            font-size: 2.5rem;
            font-weight: 700;
            color: #e0e0e0;
            text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
            transition: opacity 0.5s ease;
          }
          .app-subtitle {
            font-size: 1.1rem;
            color: #bbbbbb;
            text-shadow: 1px 1px 2px rgba(0,0,0,0.4);
            transition: opacity 0.5s ease;
          }
          @keyframes fadeInUp {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }
          .animate-on-load {
            animation: fadeInUp 0.8s ease-in-out forwards;
          }
          .animate-on-load.delay-1 {
            animation-delay: 0.2s;
          }
          .result-card-container, .upload-form-container {
            background: linear-gradient(135deg, rgba(108, 118, 125, 0.05) 0%, rgba(240, 244, 248, 0.05) 100%);
            backdrop-filter: blur(5px);
            -webkit-backdrop-filter: blur(5px);
            border-radius: 1rem;
            padding: 2rem;
          }
          .custom-card {
            background: rgba(255, 255, 255, 0.8);
            backdrop-filter: blur(3px);
            border-radius: 1rem !important;
            border: none;
          }
          .drop-zone {
            background-color: rgba(249, 250, 251, 0.1);
            border: 2px dashed rgba(156, 163, 175, 0.5);
            backdrop-filter: blur(5px);
            -webkit-backdrop-filter: blur(5px);
          }
          .drop-zone:hover, .drop-zone.drag-over {
            border-color: #6366f1;
            background-color: rgba(238, 242, 255, 0.2);
          }
          .drop-zone-text {
            color: #d1d5db;
          }
          .file-name-text {
            color: #ffffff;
          }
          .btn-analyze {
            background-color: #6366f1;
            border-color: #6366f1;
          }
          .btn-analyze:hover {
            background-color: #4338ca;
          }
          .alert-danger {
            background-color: rgba(248, 215, 218, 0.8);
            border-color: rgba(245, 198, 203, 0.8);
          }
          .navbar {
            padding-top: 1rem;
            padding-bottom: 1rem;
          }
          .btn-nav {
            color: #e0e0e0;
            font-weight: 500;
            background: transparent;
            border: none;
            padding: 0.5rem 1rem;
            transition: color 0.2s ease, background-color 0.2s ease;
          }
          .btn-nav:hover {
            color: #ffffff;
            background-color: rgba(255, 255, 255, 0.1);
            border-radius: 0.5rem;
          }
          .navbar-nav .nav-item.active .btn-nav {
            color: #ffffff;
            border-bottom: 2px solid #ffffff;
            border-radius: 0;
          }
          @media (max-width: 768px) {
            .app-container { padding: 2rem 1rem; }
            .app-title { font-size: 2rem; }
            .app-subtitle { font-size: 1rem; }
            .navbar-nav { flex-direction: column; }
            .btn-nav { width: 100%; }
          }
        `}
      </style>
      <Navbar onNavigate={setView} currentView={view} />
      <div className="app-container" style={{ paddingTop: '8rem' }}>
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-12 col-lg-8">
              {renderContent()}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
