import React from "react";

export default function JDResultCard({ result, onReset }) {
  const getScoreColor = (score) => {
    if (score >= 80) return "text-success";
    if (score >= 60) return "text-warning";
    return "text-danger";
  };

  const jdAnalysis = result.jd_analysis || {};
  const jobDescriptionText = typeof result.job_description === "string" ? result.job_description : "";

  // Extract job title from raw JD text
  const extractJobTitle = (jdText) => {
    const lines = jdText.split("\n").map(line => line.trim());
    for (let line of lines) {
      if (line.toLowerCase().startsWith("position:")) {
        return line.replace(/position:/i, "").trim();
      }
    }
    // Fallback: use first line if it looks like a title
    if (lines.length > 0 && lines[0].toLowerCase().includes("job description")) {
      return lines[0].replace(/job description/i, "").trim();
    }
    return "Unknown Position";
  };

  const jobTitle = extractJobTitle(jobDescriptionText);

  // Format skills for better display
  const formatSkills = (skills) => {
    if (!skills || !Array.isArray(skills)) return [];
    return skills.slice(0, 20); // Show max 20 skills
  };

  return (
    <div className="result-card-container">
      <div className="container" style={{ maxWidth: '800px' }}>
        {/* Job Title Display */}
        {jobTitle && jobTitle !== "Unknown Position" && (
          <div className="text-center mb-4">
            <h4 className="fw-bold">Job Title: {jobTitle}</h4>
          </div>
        )}

        {/* Score Card */}
        <div className="card shadow-lg mb-4">
          <div className="card-body text-center p-4">
            <h4 className="card-title fw-bold mb-4">Match Score: {jdAnalysis.overall_score}/100</h4>

            <div className="row">
              {[
                { label: "Skills Match", value: jdAnalysis.skill_match, color: "success" },
                { label: "Experience Match", value: jdAnalysis.experience_match, color: "info" },
                { label: "Education Match", value: jdAnalysis.education_match, color: "warning" },
                { label: "Keyword Match", value: jdAnalysis.keyword_match, color: "primary" }
              ].map((item, index) => (
                <div key={index} className="col-md-3 col-6 mb-3">
                  <h6 className="mb-2">{item.label}</h6>
                  <div
                    className={`mx-auto rounded-circle d-flex align-items-center justify-content-center ${getScoreColor(item.value)}`}
                    style={{
                      width: "70px",
                      height: "70px",
                      background: "linear-gradient(135deg, #2c3e50, #34495e)",
                      margin: "0 auto"
                    }}
                  >
                    <span className="fw-bold text-white">{item.value}%</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Skills Analysis */}
        <div className="row">
          {/* Matching Skills */}
          {jdAnalysis.matching_skills && jdAnalysis.matching_skills.length > 0 && (
            <div className="col-md-6 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title fw-bold text-success">
                    ✅ Matching Skills ({jdAnalysis.matching_skills.length})
                  </h5>
                  <p className="text-muted mb-3">These skills match the job requirements:</p>
                  <div className="d-flex flex-wrap gap-2">
                    {formatSkills(jdAnalysis.matching_skills).map((skill, index) => (
                      <span key={index} className="badge bg-success">
                        {skill}
                      </span>
                    ))}
                    {jdAnalysis.matching_skills.length > 20 && (
                      <span className="badge bg-info">
                        +{jdAnalysis.matching_skills.length - 20} more
                      </span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Missing Skills */}
          {jdAnalysis.missing_skills && jdAnalysis.missing_skills.length > 0 && (
            <div className="col-md-6 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title fw-bold text-danger">
                    ❌ Missing Skills ({jdAnalysis.missing_skills.length})
                  </h5>
                  <p className="text-muted mb-3">These skills are required but not found:</p>
                  <div className="d-flex flex-wrap gap-2">
                    {formatSkills(jdAnalysis.missing_skills).map((skill, index) => (
                      <span key={index} className="badge bg-danger">
                        {skill}
                      </span>
                    ))}
                    {jdAnalysis.missing_skills.length > 20 && (
                      <span className="badge bg-warning">
                        +{jdAnalysis.missing_skills.length - 20} more
                      </span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Experience & Education Summary */}
        <div className="row">
          {/* Experience Summary */}
          <div className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h6 className="fw-bold">Experience Match</h6>
                <div className="progress mb-2" style={{ height: "20px" }}>
                  <div
                    className="progress-bar bg-info"
                    style={{ width: `${jdAnalysis.experience_match}%` }}
                  ></div>
                </div>
                <small className="text-muted">
                  Your experience: {result.experience || 0} years | 
                  Required: {result.required_experience || "Not specified"} years
                </small>
              </div>
            </div>
          </div>

          {/* Education Summary */}
          <div className="col-md-6 mb-4">
            <div className="card shadow-sm">
              <div className="card-body">
                <h6 className="fw-bold">Education Match</h6>
                <div className="progress mb-2" style={{ height: "20px" }}>
                  <div
                    className="progress-bar bg-warning"
                    style={{ width: `${jdAnalysis.education_match}%` }}
                  ></div>
                </div>
                <small className="text-muted">
                  {jdAnalysis.education_match >= 50 ? "✅ Education requirements met" : "⚠️ Check education requirements"}
                </small>
              </div>
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="text-center mt-4">
          <button className="btn btn-primary me-3" onClick={onReset}>
            Analyze Another Resume
          </button>
          <button className="btn btn-outline-secondary" onClick={() => window.print()}>
            Print Report
          </button>
        </div>
      </div>
    </div>
  );
}
