import React, { useState } from "react";
import { analyzeWithJD } from "../services/api";

export default function JDUploadForm({ onUploadComplete }) {
  const [resumeFile, setResumeFile] = useState(null);
  const [jdFile, setJdFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [isResumeDragOver, setIsResumeDragOver] = useState(false);
  const [isJdDragOver, setIsJdDragOver] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!resumeFile || !jdFile) {
      setError("Please select both a resume and a job description file.");
      return;
    }

    const resumeValidTypes = [
      "application/pdf",
      "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
    ];
    
    const jdValidTypes = [
      "application/pdf",
      "text/plain",
      "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
    ];

    if (!resumeValidTypes.includes(resumeFile.type)) {
      setError("Resume must be PDF or DOCX format.");
      return;
    }

    if (!jdValidTypes.includes(jdFile.type)) {
      setError("Job description must be PDF, DOCX, or text file.");
      return;
    }

    setError("");
    setLoading(true);

    try {
      const result = await analyzeWithJD(resumeFile, jdFile);
      onUploadComplete(result);
      
      // Smoothly scroll to results when used outside modal
      const resultElement = document.getElementById("result-container");
      if (resultElement) {
        resultElement.scrollIntoView({ behavior: "smooth" });
      }
    } catch (err) {
      setError(err.message || "Failed to analyze files.");
    } finally {
      setLoading(false);
    }
  };

  const handleResumeDrop = (e) => {
    e.preventDefault();
    setIsResumeDragOver(false);
    const file = e.dataTransfer.files[0];
    if (file) setResumeFile(file);
  };

  const handleJdDrop = (e) => {
    e.preventDefault();
    setIsJdDragOver(false);
    const file = e.dataTransfer.files[0];
    if (file) setJdFile(file);
  };

  const getFileTypeText = (file) => {
    if (!file) return "";
    if (file.type === "application/pdf") return "PDF";
    if (file.type === "application/vnd.openxmlformats-officedocument.wordprocessingml.document") return "DOCX";
    if (file.type === "text/plain") return "TXT";
    return file.name.split('.').pop().toUpperCase();
  };

  return (
    <>
      <style>
        {`
        .jd-upload-container {
            padding: 1rem;
        }
        .upload-section {
            margin-bottom: 1.5rem;
        }
        .upload-title {
            color: #2c3e50;
            font-weight: 600;
            margin-bottom: 0.5rem;
            font-size: 1.1rem;
        }
        .drop-zone {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            padding: 1.5rem;
            border: 2px dashed #64676b;
            border-radius: 0.75rem;
            text-align: center;
            cursor: pointer;
            transition: all 0.2s ease;
            background-color: rgba(98, 101, 105, 0.1);
            backdrop-filter: blur(5px);
        }
        .drop-zone:hover, .drop-zone.drag-over {
            border-color: #3498db;
            background-color: rgba(52, 152, 219, 0.1);
        }
        .jd-drop-zone {
            border-color: #e74c3c;
            background-color: rgba(231, 76, 60, 0.1);
        }
        .jd-drop-zone:hover, .jd-drop-zone.drag-over {
            border-color: #c0392b;
            background-color: rgba(192, 57, 43, 0.15);
        }
        .drop-zone-text {
            color: #2c3e50;
            font-weight: 500;
            margin: 0.5rem 0;
        }
        .file-info {
            font-weight: 600;
            color: #2c3e50;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 0.5rem;
        }
        .file-type-badge {
            font-size: 0.75rem;
            padding: 0.25rem 0.5rem;
            border-radius: 0.25rem;
            background: rgba(0,0,0,0.1);
        }
        .format-hint {
            color: #7f8c8d;
            font-size: 0.85rem;
            margin-top: 0.5rem;
        }
        .btn-analyze {
            background: linear-gradient(135deg, #3498db, #2980b9);
            color: white;
            border: none;
            padding: 0.75rem 1.5rem;
            border-radius: 0.5rem;
            font-weight: 600;
            transition: all 0.3s ease;
            width: 100%;
            margin-top: 1rem;
        }
        .btn-analyze:hover:not(:disabled) {
            background: linear-gradient(135deg, #2980b9, #2471a3);
            transform: translateY(-2px);
        }
        .btn-analyze:disabled {
            background: #bdc3c7;
            cursor: not-allowed;
        }
        `}
      </style>

      <div className="jd-upload-container">
        <form onSubmit={handleSubmit}>
          {/* Resume Upload */}
          <div className="upload-section">
            <div className="upload-title">1. Upload Your Resume</div>
            <div
              className={`drop-zone ${isResumeDragOver ? "drag-over" : ""}`}
              onDragOver={(e) => { e.preventDefault(); setIsResumeDragOver(true); }}
              onDragLeave={() => setIsResumeDragOver(false)}
              onDrop={handleResumeDrop}
              onClick={() => document.getElementById("resume-upload").click()}
            >
              <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="#3498db" strokeWidth="2">
                <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                <polyline points="14 2 14 8 20 8"></polyline>
                <line x1="16" y1="13" x2="8" y2="13"></line>
                <line x1="16" y1="17" x2="8" y2="17"></line>
                <polyline points="10 9 9 9 8 9"></polyline>
              </svg>
              
              <div className="drop-zone-text">
                {resumeFile ? (
                  <div className="file-info">
                    {resumeFile.name}
                    <span className="file-type-badge">{getFileTypeText(resumeFile)}</span>
                  </div>
                ) : (
                  "Click to browse or drag resume here"
                )}
              </div>
              <div className="format-hint">PDF or DOCX format</div>
              
              <input
                id="resume-upload"
                type="file"
                className="d-none"
                accept=".pdf,.docx"
                onChange={(e) => setResumeFile(e.target.files[0])}
              />
            </div>
          </div>

          {/* Job Description Upload */}
          <div className="upload-section">
            <div className="upload-title">2. Upload Job Description</div>
            <div
              className={`drop-zone jd-drop-zone ${isJdDragOver ? "drag-over" : ""}`}
              onDragOver={(e) => { e.preventDefault(); setIsJdDragOver(true); }}
              onDragLeave={() => setIsJdDragOver(false)}
              onDrop={handleJdDrop}
              onClick={() => document.getElementById("jd-upload").click()}
            >
              <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="#e74c3c" strokeWidth="2">
                <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                <polyline points="14 2 14 8 20 8"></polyline>
                <line x1="16" y1="13" x2="8" y2="13"></line>
                <line x1="16" y1="17" x2="8" y2="17"></line>
                <polyline points="10 9 9 9 8 9"></polyline>
              </svg>
              
              <div className="drop-zone-text">
                {jdFile ? (
                  <div className="file-info">
                    {jdFile.name}
                    <span className="file-type-badge">{getFileTypeText(jdFile)}</span>
                  </div>
                ) : (
                  "Click to browse or drag job description here"
                )}
              </div>
              <div className="format-hint">PDF, DOCX, or TXT format</div>
              
              <input
                id="jd-upload"
                type="file"
                className="d-none"
                accept=".pdf,.docx,.txt"
                onChange={(e) => setJdFile(e.target.files[0])}
              />
            </div>
          </div>

          {error && (
            <div className="alert alert-danger mt-3">
              {error}
            </div>
          )}

          <button
            type="submit"
            className="btn-analyze"
            disabled={loading || !resumeFile || !jdFile}
          >
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm me-2"></span>
                Analyzing Match...
              </>
            ) : (
              "Analyze Resume Match"
            )}
          </button>
        </form>
      </div>
    </>
  );
}
